import numpy as np
from itertools import product

def solve():
    """
    Computes host predictions for novel viral contigs based on CRISPR and codon usage.
    """

    # --- Problem Parameters ---
    NUM_HOSTS = 3
    M_MAX = 1
    W_C = 0.7
    W_U = 0.3

    HOST_SPACERS = {
        0: ['ATGCTTACGA', 'TTTATATAAA'],
        1: ['GCGCCGCGGC', 'CCGGGCGGCC'],
        2: []
    }

    HOST_PATTERNS = {
        0: ['TTT', 'TTA', 'TAT', 'ATA', 'ATT', 'AAA', 'AAT'],
        1: ['GCG', 'GGC', 'CCG', 'CGC', 'GCC', 'CCC', 'GGG'],
        2: ['CTG', 'GAT', 'ATG', 'ACC', 'TCC', 'GTA', 'GTT']
    }

    TEST_CASES = [
        {
            "C": "GGGATGCTTACGATTTACGCCGTA",
            "V_pattern": ['TTT', 'TTA', 'TAT', 'ATA', 'ATT', 'AAA', 'AAT']
        },
        {
            "C": "AAAGCGCCGCGGATTGTTTTATATCAAACCC",
            "V_pattern": ['GCG', 'GGC', 'CCG', 'CGC', 'GCC', 'CCC', 'GGG']
        },
        {
            "C": "CCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCC",
            "V_pattern": ['CTG', 'GAT', 'ATG', 'ACC', 'TCC', 'GTA', 'GTT']
        },
        {
            "C": "TTTATGCTTACGGAAA",
            "V_pattern": ['GCG', 'GGC', 'CCG', 'CGC', 'GCC', 'CCC', 'GGG']
        }
    ]

    # --- Helper Functions ---
    def reverse_complement(dna_seq):
        complement_map = {'A': 'T', 'T': 'A', 'C': 'G', 'G': 'C'}
        return "".join(complement_map[base] for base in reversed(dna_seq))

    def hamming_distance(s1, s2):
        return sum(c1 != c2 for c1, c2 in zip(s1, s2))

    # --- Scoring Functions ---
    def compute_crispr_score(contig, spacers):
        if not spacers:
            return 0.0

        total_phi = 0.0
        for spacer in spacers:
            L = len(spacer)
            if L > len(contig):
                continue

            rc_spacer = reverse_complement(spacer)

            min_dist_s = L + 1
            min_dist_rc = L + 1

            for i in range(len(contig) - L + 1):
                window = contig[i:i+L]
                min_dist_s = min(min_dist_s, hamming_distance(spacer, window))
                min_dist_rc = min(min_dist_rc, hamming_distance(rc_spacer, window))

            m_star = min(min_dist_s, min_dist_rc)

            phi = 0.0
            if m_star <= M_MAX:
                phi = (L - m_star) / L
            total_phi += phi

        return total_phi / len(spacers)

    def get_codon_freq_vector(sequence, codon_map):
        counts = np.zeros(64, dtype=float)
        num_codons = len(sequence) // 3
        if num_codons == 0:
            return counts

        for i in range(num_codons):
            codon = sequence[i*3:(i+1)*3]
            if codon in codon_map:
                counts[codon_map[codon]] += 1

        return counts / num_codons

    def compute_codon_usage_score(vec1, vec2):
        norm1 = np.linalg.norm(vec1)
        norm2 = np.linalg.norm(vec2)
        if norm1 == 0 or norm2 == 0:
            return 0.0
        return np.dot(vec1, vec2) / (norm1 * norm2)

    # --- Main Logic ---

    # Pre-compute host data
    codons = [''.join(p) for p in product('ACGT', repeat=3)]
    codon_map = {codon: i for i, codon in enumerate(codons)}

    host_sequences = {h: ''.join(p) * 30 for h, p in HOST_PATTERNS.items()}
    host_freq_vectors = {
        h: get_codon_freq_vector(seq, codon_map) for h, seq in host_sequences.items()
    }

    results = []
    for test in TEST_CASES:
        contig = test["C"]
        viral_seq = ''.join(test["V_pattern"]) * 5
        viral_freq_vector = get_codon_freq_vector(viral_seq, codon_map)

        host_total_scores = []
        for h in range(NUM_HOSTS):
            e_h = compute_crispr_score(contig, HOST_SPACERS[h])
            u_h = compute_codon_usage_score(host_freq_vectors[h], viral_freq_vector)
            t_h = W_C * e_h + W_U * u_h
            host_total_scores.append(t_h)

        prediction = np.argmax(host_total_scores)
        results.append(prediction)

    # Format and print the final output
    print(f"[{','.join(map(str, results))}]")

solve()
