import numpy as np

def solve():
    """
    Solves the adversarial ligand construction problem for all test cases.
    """

    test_cases = [
        # Test case 1
        {
            "rec_acceptors": np.array([
                [0.0, 0.0, 0.0], [4.0, 0.0, 0.0], [0.0, 4.0, 0.0],
                [0.0, 0.0, 4.0], [3.0, 3.0, 0.0]
            ]),
            "rec_donors": np.empty((0, 3)),
            "params": {
                "d_hb": 3.5, "d_clash": 1.2, "w_hb": 1.0, "w_clash": 0.05,
                "r0": 2.8, "sigma": 0.30, "e_hb": 3.0, "p_polar": 2.5,
                "p_rot": 0.4, "p_clash": 10.0
            },
            "thresholds": {"S_target": 5, "E_bad": 5.0}
        },
        # Test case 2
        {
            "rec_acceptors": np.array([[0.0, 0.0, 0.0], [5.0, 0.0, 0.0]]),
            "rec_donors": np.array([[0.0, 5.0, 0.0]]),
            "params": {
                "d_hb": 2.8, "d_clash": 1.2, "w_hb": 1.0, "w_clash": 0.05,
                "r0": 2.8, "sigma": 0.30, "e_hb": 3.0, "p_polar": 2.5,
                "p_rot": 0.4, "p_clash": 10.0
            },
            "thresholds": {"S_target": 3, "E_bad": 2.0}
        },
        # Test case 3
        {
            "rec_acceptors": np.array([[0.0, 0.0, 0.0], [2.0, 0.0, 0.0], [0.0, 2.0, 0.0]]),
            "rec_donors": np.array([[1.0, 1.0, 0.0], [1.0, -1.0, 0.0], [3.0, 1.0, 0.0]]),
            "params": {
                "d_hb": 3.8, "d_clash": 1.2, "w_hb": 1.0, "w_clash": 0.05,
                "r0": 2.8, "sigma": 0.35, "e_hb": 2.8, "p_polar": 2.0,
                "p_rot": 0.4, "p_clash": 10.0
            },
            "thresholds": {"S_target": 6, "E_bad": 8.0}
        }
    ]

    results = []
    for case in test_cases:
        ligand_donors, ligand_acceptors = construct_adversarial_ligand(case)
        S, E_true = calculate_scores(
            ligand_donors, ligand_acceptors,
            case["rec_donors"], case["rec_acceptors"],
            case["params"]
        )

        is_malicious = (S = case["thresholds"]["S_target"] and 
                        E_true = case["thresholds"]["E_bad"])
        results.append(str(is_malicious))

    print(f"[{','.join(results)}]")

def construct_adversarial_ligand(case_data):
    """
    Constructs a ligand by placing complementary atoms near receptor features.
    The placement strategy is adversarial: place at a distance d_clash
    from the target atom in a direction that does not clash with other receptor atoms.
    """
    params = case_data["params"]
    d_clash = params["d_clash"]

    rec_acceptors = case_data["rec_acceptors"]
    rec_donors = case_data["rec_donors"]

    all_rec_pts = []
    if rec_acceptors.shape[0]  0:
        all_rec_pts.append(rec_acceptors)
    if rec_donors.shape[0]  0:
        all_rec_pts.append(rec_donors)

    if not all_rec_pts:
        return np.empty((0,3)), np.empty((0,3))

    all_rec_pts = np.vstack(all_rec_pts)

    deltas = np.array([
        [d_clash, 0, 0], [0, d_clash, 0], [0, 0, d_clash],
        [-d_clash, 0, 0], [0, -d_clash, 0], [0, 0, -d_clash]
    ])

    def find_non_clashing_pos(target_rec_pt, all_rec_pts, deltas):
        for delta in deltas:
            candidate_pos = target_rec_pt + delta
            is_clashing = False
            # Check candidate against all receptor points
            distances = np.linalg.norm(all_rec_pts - candidate_pos, axis=1)
            if np.any(distances  d_clash):
                is_clashing = True

            if not is_clashing:
                return candidate_pos
        # Fallback if no non-clashing position is found (unlikely for these test cases)
        return target_rec_pt + deltas[0]

    ligand_donors = []
    for rec_acc in rec_acceptors:
        pos = find_non_clashing_pos(rec_acc, all_rec_pts, deltas)
        ligand_donors.append(pos)

    ligand_acceptors = []
    for rec_don in rec_donors:
        pos = find_non_clashing_pos(rec_don, all_rec_pts, deltas)
        ligand_acceptors.append(pos)

    return np.array(ligand_donors), np.array(ligand_acceptors)


def calculate_scores(lig_donors, lig_acceptors, rec_donors, rec_acceptors, params):
    """
    Calculates the empirical score (S) and physical energy proxy (E_true).
    """
    # Unpack parameters
    d_hb = params["d_hb"]
    d_clash = params["d_clash"]
    w_hb = params["w_hb"]
    w_clash = params["w_clash"]
    r0 = params["r0"]
    sigma = params["sigma"]
    e_hb = params["e_hb"]
    p_polar = params["p_polar"]
    p_rot = params["p_rot"]
    p_clash = params["p_clash"]

    # Ligand properties
    n_atoms = len(lig_donors) + len(lig_acceptors)
    n_polar = n_atoms

    # Calculate N_hb and sum of g(r)
    N_hb = 0
    sum_g_r = 0.0

    def g(r, r0, sigma):
        return np.exp(-((r - r0)**2) / (2 * sigma**2))

    # Receptor Acceptors vs Ligand Donors
    if rec_acceptors.shape[0]  0 and lig_donors.shape[0]  0:
        for lig_d in lig_donors:
            for rec_a in rec_acceptors:
                dist = np.linalg.norm(lig_d - rec_a)
                if dist = d_hb:
                    N_hb += 1
                sum_g_r += g(dist, r0, sigma)

    # Receptor Donors vs Ligand Acceptors
    if rec_donors.shape[0]  0 and lig_acceptors.shape[0]  0:
        for lig_a in lig_acceptors:
            for rec_d in rec_donors:
                dist = np.linalg.norm(lig_a - rec_d)
                if dist = d_hb:
                    N_hb += 1
                sum_g_r += g(dist, r0, sigma)

    # Calculate N_clash
    N_clash = 0

    all_rec_pts = []
    if rec_acceptors.shape[0]  0: all_rec_pts.append(rec_acceptors)
    if rec_donors.shape[0]  0: all_rec_pts.append(rec_donors)
    all_rec_pts = np.vstack(all_rec_pts) if all_rec_pts else np.empty((0,3))

    all_lig_pts = []
    if lig_donors.shape[0]  0: all_lig_pts.append(lig_donors)
    if lig_acceptors.shape[0]  0: all_lig_pts.append(lig_acceptors)
    all_lig_pts = np.vstack(all_lig_pts) if all_lig_pts else np.empty((0,3))

    if all_rec_pts.shape[0]  0:
        for lig_pt in all_lig_pts:
            min_dist = np.min(np.linalg.norm(all_rec_pts - lig_pt, axis=1))
            if min_dist  d_clash:
                N_clash += 1

    # Final score calculations
    S = w_hb * N_hb - w_clash * N_clash

    E_true = -e_hb * sum_g_r \
             + p_polar * n_polar \
             + p_rot * max(n_atoms - 1, 0) \
             + p_clash * N_clash

    return S, E_true

solve()
