import numpy as np
import re

def solve():
    # Registry data defined as per the problem statement
    ISOFORM_SEQUENCES = {
        "P04637-1": "MEEPQSDPSVEPPLSQETFSDLWKLL",
        "P04637-2": "MEEPQSDPSVEPPLSQETFSDLWKL",
        "P38398-1": "MDLSALRVEEVQNVINAMQKILECPICLE",
        "P01106-1": "MPLNVSFTNRNYDLDYDSVQPY",
        "P01106-2": "MPLNVSFTNRNYDLDYDSVQP",
        "Q9Y243-1": "MSSILPFTPPVKRLLGWKHPGKQL",
    }

    UNIPROT_SECONDARY_TO_PRIMARY = {
        "Q13616": "P04637", "Q65484": "P38398", "P12524": "P01106", "O15162": "Q9Y243",
    }

    REFSEQ_TO_UNIPROT = {
        "NP_000537": "P04637", "NP_009225": "P38398", "NP_002458": "P01106", "NP_123456": "Q9Y243",
    }

    GI_TO_REFSEQ = {
        "gi|4557321": "NP_000537.2", "gi|8404561": "NP_009225.1", "gi|3154298": "NP_002458.1", "gi|15726711": "NP_123456.1",
    }

    # Gene symbol mapping is case-insensitive. We store keys in uppercase.
    GENE_TO_UNIPROT = {
        "TP53": "P04637", "P53": "P04637", "TRP53": "P04637",
        "BRCA1": "P38398", "BRCC1": "P38398",
        "MYC": "P01106", "C-MYC": "P01106",
        "SMAD7": "Q9Y243", "MADH7": "Q9Y243",
    }

    # FNV-1a hash parameters
    FNV_PRIME = np.uint64(1099511628211)
    FNV_OFFSET_BASIS = np.uint64(1469598103934665603)

    def fnv1a_hash(sequence_str):
        """Computes the 64-bit FNV-1a hash of a string."""
        h = FNV_OFFSET_BASIS
        for byte in sequence_str.encode('ascii'):
            h ^= np.uint64(byte)
            h *= FNV_PRIME
        return int(h)

    def get_canonical_isoform_key(primary_acc):
        """Finds the canonical isoform for a given primary UniProt accession."""
        key_iso1 = f"{primary_acc}-1"
        if key_iso1 in ISOFORM_SEQUENCES:
            return key_iso1

        isoform_indices = []
        for key in ISOFORM_SEQUENCES:
            if key.startswith(f"{primary_acc}-"):
                try:
                    index_part = key.split('-')[1]
                    isoform_indices.append(int(index_part))
                except (ValueError, IndexError):
                    continue

        if not isoform_indices:
            return None

        min_index = min(isoform_indices)
        return f"{primary_acc}-{min_index}"

    def resolve_token(token):
        """Resolves an input token to a primary UniProt accession."""
        token = token.strip()

        # Rule 1: GI pattern
        if token.startswith("gi|") and token.split('|')[1].isdigit():
            refseq_ver = GI_TO_REFSEQ.get(token)
            if refseq_ver:
                refseq_root = refseq_ver.split('.')[0]
                return REFSEQ_TO_UNIPROT.get(refseq_root)
            return None

        # Rule 2: RefSeq pattern
        if re.match(r"^NP_\d+\.\d+$", token):
            refseq_root = token.split('.')[0]
            return REFSEQ_TO_UNIPROT.get(refseq_root)

        # Rule 3: UniProt with isoform pattern
        match = re.match(r"^([A-Z0-9]+)-(\d+)$", token)
        if match:
            base_acc = match.group(1)
            return UNIPROT_SECONDARY_TO_PRIMARY.get(base_acc, base_acc)

        # Rule 4: UniProt without isoform
        # A simple heuristic: if it's alphanumeric and in one of the relevant DBs
        if re.match(r"^[A-Z0-9]+$", token):
             # Check if it's secondary
            if token in UNIPROT_SECONDARY_TO_PRIMARY:
                return UNIPROT_SECONDARY_TO_PRIMARY[token]
            # Check if it's a known primary through its isoforms
            for key in ISOFORM_SEQUENCES:
                if key.startswith(token + '-'):
                    return token

        # Rule 5: Gene symbol
        primary_acc = GENE_TO_UNIPROT.get(token.upper())
        if primary_acc:
            return primary_acc

        return None

    def get_result(token):
        """Main logic to process one token and return the result."""
        primary_acc = resolve_token(token)
        if not primary_acc:
            return [-1, -1]

        canonical_key = get_canonical_isoform_key(primary_acc)
        if not canonical_key:
            return [-1, -1]

        sequence = ISOFORM_SEQUENCES.get(canonical_key)
        if not sequence:
            return [-1, -1]

        length = len(sequence)
        hash_val = fnv1a_hash(sequence)

        return [length, hash_val]

    # Test suite from the problem description
    test_cases = [
        "TP53",
        "Q65484",
        "NP_002458.1",
        "gi|15726711",
        "P04637-2",
        "p53",
        "gi|99999999"
    ]

    results = []
    for case in test_cases:
        result = get_result(case)
        results.append(str(result))

    # Final print statement in the exact required format
    print(f"[{','.join(results)}]")

solve()
