import numpy as np

def solve():
    """
    Solves the Schelling segregation model for the provided test suite.
    """
    test_cases = [
        # Case A
        {'n': 10, 'tau': 0.5, 'T_max': 200, 'r': 2, 'N1': 45, 'N2': 45, 
         'influencers': [((0, 0), 1), ((9, 9), 1)]},
        # Case B
        {'n': 10, 'tau': 0.5, 'T_max': 200, 'r': 2, 'N1': 45, 'N2': 45, 
         'influencers': [((5, 5), 2), ((5, 6), 2)]},
        # Case C
        {'n': 10, 'tau': 0.75, 'T_max': 200, 'r': 2, 'N1': 50, 'N2': 40, 
         'influencers': [((0, 0), 1), ((0, 9), 2)]},
        # Case D
        {'n': 10, 'tau': 0.5, 'T_max': 200, 'r': 2, 'N1': 50, 'N2': 50, 
         'influencers': [((5, 5), 1)]},
    ]

    results = []
    for case in test_cases:
        result = process_case(case)
        results.append(result)

    print(f"[{','.join([f'{x:.4f}' for x in results])}]")

def process_case(params):
    """
    Processes a single test case from initialization to final metric calculation.
    """
    n, N1, N2, tau, T_max, r = params['n'], params['N1'], params['N2'], params['tau'], params['T_max'], params['r']
    influencers = params['influencers']

    grid, is_influencer, influencer_info = initialize_grid(n, N1, N2, influencers)
    final_grid = run_simulation(grid, is_influencer, n, tau, T_max)
    metric = calculate_metrics(final_grid, N1, N2, influencer_info, n, r)

    return metric

def get_moore_neighbors(r, c, n):
    """Gets Moore neighborhood coordinates with toroidal boundary."""
    neighbors = []
    for dr in [-1, 0, 1]:
        for dc in [-1, 0, 1]:
            if dr == 0 and dc == 0:
                continue
            nr, nc = (r + dr) % n, (c + dc) % n
            neighbors.append((nr, nc))
    return neighbors

def calculate_homophily(grid, r, c, agent_type, n):
    """
    Calculates the local same-type fraction for an agent of `agent_type` at (r,c).
    """
    neighbors = get_moore_neighbors(r, c, n)
    same_type_neighbors = 0
    occupied_neighbors = 0

    for nr, nc in neighbors:
        neighbor_type = grid[nr, nc]
        if neighbor_type > 0:
            occupied_neighbors += 1
            if neighbor_type == agent_type:
                same_type_neighbors += 1

    if occupied_neighbors == 0:
        return 1.0
    return same_type_neighbors / occupied_neighbors

def initialize_grid(n, N1, N2, influencers):
    """Initializes the grid according to the problem statement."""
    grid = np.zeros((n, n), dtype=int)
    is_influencer = np.zeros((n, n), dtype=bool)

    rem_N1, rem_N2 = N1, N2
    influencer_info = []

    for pos, type in influencers:
        grid[pos] = type
        is_influencer[pos] = True
        influencer_info.append((pos, type))
        if type == 1:
            rem_N1 -= 1
        else:
            rem_N2 -= 1

    next_type_in_sequence = 1
    for r in range(n):
        for c in range(n):
            if rem_N1 == 0 and rem_N2 == 0:
                break
            if grid[r, c] == 0:
                type_to_place = 0
                if next_type_in_sequence == 1:
                    if rem_N1 > 0:
                        type_to_place = 1
                    elif rem_N2 > 0:
                        type_to_place = 2
                else: 
                    if rem_N2 > 0:
                        type_to_place = 2
                    elif rem_N1 > 0:
                        type_to_place = 1

                if type_to_place == 1:
                    grid[r, c] = 1
                    rem_N1 -= 1
                elif type_to_place == 2:
                    grid[r, c] = 2
                    rem_N2 -= 1

                next_type_in_sequence = 3 - next_type_in_sequence
        if rem_N1 == 0 and rem_N2 == 0:
            break

    return grid, is_influencer, influencer_info

def run_simulation(grid, is_influencer, n, tau, T_max):
    """Runs the Schelling model simulation with sequential updates."""
    current_grid = grid.copy()
    for _ in range(T_max):
        moved_in_sweep = False
        for r in range(n):
            for c in range(n):
                agent_type = current_grid[r, c]
                if agent_type > 0 and not is_influencer[r, c]:
                    current_f = calculate_homophily(current_grid, r, c, agent_type, n)

                    if current_f < tau:
                        empty_cells = list(zip(*np.where(current_grid == 0)))
                        if not empty_cells:
                            continue

                        max_f = -1.0
                        best_dest = None

                        hypothetical_grid = current_grid.copy()
                        hypothetical_grid[r, c] = 0

                        for dest_r, dest_c in empty_cells:
                            f_dest = calculate_homophily(hypothetical_grid, dest_r, dest_c, agent_type, n)
                            if f_dest > max_f:
                                max_f = f_dest
                                best_dest = (dest_r, dest_c)
                            elif f_dest == max_f:
                                if (dest_r, dest_c) < best_dest:
                                    best_dest = (dest_r, dest_c)

                        if best_dest is not None and max_f > current_f:
                            current_grid[best_dest] = agent_type
                            current_grid[r, c] = 0
                            moved_in_sweep = True

        if not moved_in_sweep:
            break

    return current_grid

def torus_chebyshev_dist(p1, p2, n):
    """Calculates Chebyshev distance on an n x n torus."""
    dr = abs(p1[0] - p2[0])
    dc = abs(p1[1] - p2[1])
    torus_dr = min(dr, n - dr)
    torus_dc = min(dc, n - dc)
    return max(torus_dr, torus_dc)

def calculate_metrics(final_grid, N1, N2, influencer_info, n, r):
    """Calculates the final test-case result (mean anchoring lift)."""
    M = N1 + N2
    if M == 0: return 0.0

    if not influencer_info: # This case is not in the test suite but handled for completeness
        total_homophily = 0.0
        occupied_coords = np.argwhere(final_grid > 0)
        for r_occ, c_occ in occupied_coords:
            agent_type = final_grid[r_occ, c_occ]
            total_homophily += calculate_homophily(final_grid, r_occ, c_occ, agent_type, n)
        return total_homophily / M

    lifts = []
    G1 = N1 / M
    G2 = N2 / M
    G_map = {1: G1, 2: G2}

    for inf_pos, inf_type in influencer_info:
        local_same_count = 0
        local_total_count = 0
        for r_cell in range(n):
            for c_cell in range(n):
                if torus_chebyshev_dist(inf_pos, (r_cell, c_cell), n) <= r:
                    cell_type = final_grid[r_cell, c_cell]
                    if cell_type > 0:
                        local_total_count += 1
                        if cell_type == inf_type:
                            local_same_count += 1

        Lk = local_same_count / local_total_count if local_total_count > 0 else 0.0
        Gs_k = G_map[inf_type]
        lift = Lk - Gs_k
        lifts.append(lift)

    return np.mean(lifts)

solve()

