import numpy as np

def solve():
    """
    Main function to solve all test cases and print the results.
    """

    class ChannelModel:
        """
        A class to encapsulate the behavior of a single marketing channel.
        """
        def __init__(self, ch_type, params, u_i):
            self.ch_type = ch_type
            self.params = params
            self.u_i = u_i if u_i is not None else np.inf

            # Pre-calculate derivatives at boundaries for efficiency
            self.deriv_at_0 = self._f_prime(1e-12) # Use small epsilon for S-type
            if self.ch_type == 'S':
                self.deriv_at_0 = np.inf
            else:
                 self.deriv_at_0 = self._f_prime(0)

            self.deriv_at_u = 0
            if self.u_i < np.inf:
                self.deriv_at_u = self._f_prime(self.u_i)

        def f(self, x):
            """Calculates the number of conversions for a given spend x."""
            if x < 0: x = 0
            if self.ch_type == 'L':
                a, b = self.params
                if a == 0: return 0.0
                return a * np.log(1 + b * x)
            elif self.ch_type == 'S':
                c, = self.params
                return c * np.sqrt(x)
            elif self.ch_type == 'E':
                d, e = self.params
                return d * (1 - np.exp(-e * x))
            return 0.0

        def _f_prime(self, x):
            """Calculates the marginal conversion rate (derivative) at spend x."""
            if self.ch_type == 'L':
                a, b = self.params
                if a == 0: return 0.0
                return (a * b) / (1 + b * x)
            elif self.ch_type == 'S':
                c, = self.params
                if x == 0: return np.inf
                return c / (2 * np.sqrt(x))
            elif self.ch_type == 'E':
                d, e = self.params
                return d * e * np.exp(-e * x)
            return 0.0

        def _f_prime_inv(self, lam):
            """Calculates spend x for a given marginal return lambda."""
            if lam <= 0: return np.inf
            if self.ch_type == 'L':
                a, b = self.params
                if a == 0: return 0.0
                if lam >= a * b: return 0.0
                return a / lam - 1 / b
            elif self.ch_type == 'S':
                c, = self.params
                return (c / (2 * lam))**2
            elif self.ch_type == 'E':
                d, e = self.params
                if lam >= d * e: return 0.0
                return np.log(d * e / lam) / e
            return 0.0

        def get_spend(self, lam):
            """Determines optimal spend for a given lambda."""
            if lam >= self.deriv_at_0:
                return 0.0
            if lam <= self.deriv_at_u and self.u_i < np.inf:
                return self.u_i

            unbounded_spend = self._f_prime_inv(lam)
            return min(self.u_i, max(0, unbounded_spend))

    def solve_one_case(budget, channels_data):
        """
        Solves a single instance of the allocation problem.
        """
        if budget == 0.0:
            return 0.0

        channels = [ChannelModel(c_type, params, u_i) for c_type, params, u_i in channels_data]

        total_upper_bound = sum(ch.u_i for ch in channels if ch.u_i < np.inf)
        if all(ch.u_i < np.inf for ch in channels) and budget >= total_upper_bound:
            total_conversions = sum(ch.f(ch.u_i) for ch in channels)
            return total_conversions

        # Bisection search to find the optimal lambda
        low_lam = 1e-9
        high_lam = 1e6 # A sufficiently large upper bound for lambda

        for _ in range(100): # 100 iterations are sufficient for double precision
            mid_lam = (low_lam + high_lam) / 2
            current_spend = sum(ch.get_spend(mid_lam) for ch in channels)

            if current_spend > budget:
                low_lam = mid_lam
            else:
                high_lam = mid_lam

        final_lambda = (low_lam + high_lam) / 2

        # Calculate final conversions with the optimal lambda
        total_conversions = sum(ch.f(ch.get_spend(final_lambda)) for ch in channels)
        return total_conversions

    test_cases = [
        {
            "B": 100.0,
            "channels": [
                ('L', (120.0, 0.015), None),
                ('L', (80.0, 0.03), None),
                ('E', (200.0, 0.02), None),
            ]
        },
        {
            "B": 0.0,
            "channels": [
                ('L', (100.0, 0.02), None),
                ('S', (50.0,), None),
            ]
        },
        {
            "B": 50.0,
            "channels": [
                ('S', (40.0,), 10.0),
                ('S', (30.0,), 5.0),
            ]
        },
        {
            "B": 120.0,
            "channels": [
                ('L', (150.0, 0.01), 80.0),
                ('S', (60.0,), None),
                ('E', (180.0, 0.05), 30.0),
                ('L', (0.0, 0.02), None),
            ]
        },
    ]

    results = []
    for case in test_cases:
        result = solve_one_case(case["B"], case["channels"])
        results.append(result)

    # Format the final output string
    formatted_results = [f"{res:.6f}" for res in results]
    print(f"[{','.join(formatted_results)}]")

solve()
