import numpy as np

# A strict Russian professor insists on scientific correctness from first principles.

class Dual:
    """
    Represents a dual number for forward-mode automatic differentiation.
    A dual number has a real part (value) and an infinitesimal part (derivative).
    d = value + derivative * epsilon, where epsilon^2 = 0.
    """
    def __init__(self, value, derivative=0.0):
        self.value = float(value)
        self.derivative = float(derivative)

    def __repr__(self):
        return f"Dual({self.value}, {self.derivative})"

    def __add__(self, other):
        if isinstance(other, Dual):
            return Dual(self.value + other.value, self.derivative + other.derivative)
        return Dual(self.value + other, self.derivative)

    def __radd__(self, other):
        return self.__add__(other)

    def __sub__(self, other):
        if isinstance(other, Dual):
            return Dual(self.value - other.value, self.derivative - other.derivative)
        return Dual(self.value - other, self.derivative)

    def __rsub__(self, other):
        return Dual(other - self.value, -self.derivative)

    def __mul__(self, other):
        if isinstance(other, Dual):
            # Product rule: (uv)' = u'v + uv'
            return Dual(self.value * other.value, 
                        self.derivative * other.value + self.value * other.derivative)
        return Dual(self.value * other, self.derivative * other)

    def __rmul__(self, other):
        return self.__mul__(other)

    def __pow__(self, exponent):
        if isinstance(exponent, (int, float)):
            # Power rule for constant exponent: (u^c)' = c*u^(c-1)*u'
            val = self.value ** exponent
            der = exponent * (self.value ** (exponent - 1)) * self.derivative
            return Dual(val, der)
        raise NotImplementedError("Dual to the power of Dual is not implemented.")

    def __truediv__(self, other):
        if isinstance(other, Dual):
            # Quotient rule: (u/v)' = (u'v - uv') / v^2
            val = self.value / other.value
            der = (self.derivative * other.value - self.value * other.derivative) / (other.value ** 2)
            return Dual(val, der)
        return Dual(self.value / other, self.derivative / other)

    def __rtruediv__(self, other):
        val = other / self.value
        der = (-other * self.derivative) / (self.value ** 2)
        return Dual(val, der)

    def __neg__(self):
        return Dual(-self.value, -self.derivative)

# Polymorphic elementary functions that work with both floats and Dual numbers.
def exp(d):
    if not isinstance(d, Dual): return np.exp(d)
    val = np.exp(d.value)
    der = val * d.derivative
    return Dual(val, der)

def sin(d):
    if not isinstance(d, Dual): return np.sin(d)
    val = np.sin(d.value)
    der = np.cos(d.value) * d.derivative
    return Dual(val, der)

def cos(d):
    if not isinstance(d, Dual): return np.cos(d)
    val = np.cos(d.value)
    der = -np.sin(d.value) * d.derivative
    return Dual(val, der)

def log(d):
    if not isinstance(d, Dual): return np.log(d)
    val = np.log(d.value)
    der = (1 / d.value) * d.derivative
    return Dual(val, der)

def tanh(d):
    if not isinstance(d, Dual): return np.tanh(d)
    val = np.tanh(d.value)
    # Derivative of tanh(x) is sech^2(x) = 1 - tanh^2(x)
    der = (1 - val**2) * d.derivative
    return Dual(val, der)

# Problem-specific nonlinear mappings
def g(x_vec):
    """ Intermediate mapping g: R^3 -> R^3 """
    x1, x2, x3 = x_vec[0], x_vec[1], x_vec[2]
    return [
        exp(x1 * x2),
        sin(x2 + x3),
        x1**2 + x3
    ]

def h(u_vec):
    """ Final mapping h: R^3 -> R^2 """
    u1, u2, u3 = u_vec[0], u_vec[1], u_vec[2]
    return [
        u1 * cos(u2) + u3**3,
        log(1 + u1**2 + u2**2) + tanh(u3)
    ]

def F(x_vec):
    """ Composed mapping F = h(g(x)) """
    return h(g(x_vec))

def compute_jacobian_ad(x_val):
    """Computes the Jacobian using forward-mode automatic differentiation."""
    n_in = len(x_val)
    # Dynamically determine output dimension by a sample evaluation
    n_out = len(F(x_val))
    J_ad = np.zeros((n_out, n_in))

    for j in range(n_in):
        # Create dual number inputs with a seed for the j-th partial derivative
        x_dual = [Dual(x_val[i], 1.0 if i == j else 0.0) for i in range(n_in)]

        # Evaluate the function with dual numbers
        F_dual = F(x_dual)

        # The derivative part of the output is the j-th column of the Jacobian
        for i in range(n_out):
            J_ad[i, j] = F_dual[i].derivative

    return J_ad

def compute_jacobian_fd(x_val, h_step):
    """Computes the Jacobian using the central finite-difference formula."""
    x_val = np.array(x_val, dtype=float)
    n_in = len(x_val)
    n_out = len(F(x_val))
    J_fd = np.zeros((n_out, n_in))

    for j in range(n_in):
        e_j = np.zeros(n_in)
        e_j[j] = 1.0

        x_fwd = x_val + h_step * e_j
        x_bwd = x_val - h_step * e_j

        F_fwd = np.array(F(x_fwd))
        F_bwd = np.array(F(x_bwd))

        column = (F_fwd - F_bwd) / (2 * h_step)
        J_fd[:, j] = column

    return J_fd

def solve():
    # Define the test cases from the problem statement.
    test_cases = [
        [0.2, -0.3, 0.5],
        [1e-8, -1e-8, 1e-8],
        [1.5, 0.7, -1.2],
        [-2.0, 0.4, 0.3]
    ]
    step_sizes = [1e-6, 1e-8]

    results = []
    for x_vec in test_cases:
        # Compute the "exact" Jacobian using Automatic Differentiation
        J_ad = compute_jacobian_ad(x_vec)
        norm_J_ad = np.linalg.norm(J_ad, 'fro')

        for h in step_sizes:
            # Compute the approximate Jacobian using Finite Differences
            J_fd = compute_jacobian_fd(x_vec, h)

            # Calculate the relative Frobenius-norm error
            norm_diff = np.linalg.norm(J_fd - J_ad, 'fro')
            error = norm_diff / max(1.0, norm_J_ad)
            results.append(error)

    # Final print statement in the exact required format.
    print(f"[{','.join(map(str, results))}]")

solve()
