import numpy as np

class BTreeNode:
    def __init__(self, t, leaf=False):
        self.t = t
        self.keys = []
        self.children = []
        self.leaf = leaf

    def __repr__(self):
        return f"Node(keys={self.keys}, leaf={self.leaf})"


class BTree:
    def __init__(self, t):
        self.t = t
        self.root = BTreeNode(t, leaf=True)
        self.rotations = 0
        self.merges = 0

    # Standard B-tree insertion (CLRS style)
    def insert(self, k):
        root = self.root
        if len(root.keys) == 2*self.t - 1:
            s = BTreeNode(self.t, leaf=False)
            s.children.append(root)
            self._split_child(s, 0)
            self.root = s
            self._insert_nonfull(s, k)
        else:
            self._insert_nonfull(root, k)

    def _insert_nonfull(self, x, k):
        i = len(x.keys) - 1
        if x.leaf:
            # insert in sorted order
            x.keys.append(k)
            i = len(x.keys) - 2
            while i >= 0 and x.keys[i] > k:
                x.keys[i+1] = x.keys[i]
                i -= 1
            x.keys[i+1] = k
        else:
            while i >= 0 and k  x.keys[i]:
                i -= 1
            i += 1
            if len(x.children[i].keys) == 2*self.t - 1:
                self._split_child(x, i)
                if k > x.keys[i]:
                    i += 1
            self._insert_nonfull(x.children[i], k)

    def _split_child(self, x, i):
        t = self.t
        y = x.children[i]
        z = BTreeNode(t, leaf=y.leaf)
        # median key at index t-1
        median = y.keys[t-1]
        # distribute keys
        z.keys = y.keys[t:]
        y.keys = y.keys[:t-1]
        if not y.leaf:
            z.children = y.children[t:]
            y.children = y.children[:t]
        x.children.insert(i+1, z)
        x.keys.insert(i, median)

    # Search returns node containing key or leaf where it would be inserted
    def search(self, k):
        return self._search_node(self.root, k)

    def _search_node(self, x, k):
        i = 0
        while i  len(x.keys) and k > x.keys[i]:
            i += 1
        if i  len(x.keys) and k == x.keys[i]:
            return x, i
        if x.leaf:
            return x, None
        else:
            return self._search_node(x.children[i], k)

    # Find parent of a given node and child's index
    def find_parent(self, current, target):
        if current.leaf:
            return None, None
        for i, child in enumerate(current.children):
            if child is target:
                return current, i
            if not child.leaf:
                p, idx = self.find_parent(child, target)
                if p is not None:
                    return p, idx
            else:
                # continue
                pass
        # Try descending anyway based on key ranges to find parent in deeper subtrees
        for i, child in enumerate(current.children):
            if child is target:
                return current, i
            p, idx = self.find_parent(child, target)
            if p is not None:
                return p, idx
        return None, None

    # Helpers for rotations
    def rotate_from_left(self, parent, idx):
        # Borrow from left sibling into child at idx
        left = parent.children[idx-1]
        node = parent.children[idx]
        # Move parent separator down into node, replace parent separator with left's max key
        # Also move child pointer for internal nodes
        if node.leaf:
            # insert parent key at beginning
            node.keys.insert(0, parent.keys[idx-1])
            # replace parent key with left's last key
            parent.keys[idx-1] = left.keys.pop()
        else:
            # node gains parent.sep at beginning
            node.keys.insert(0, parent.keys[idx-1])
            # node must take left's last child to the front
            child_to_move = left.children.pop()
            node.children.insert(0, child_to_move)
            # parent sep replaced with left's last key
            parent.keys[idx-1] = left.keys.pop()
        self.rotations += 1

    def rotate_from_right(self, parent, idx):
        right = parent.children[idx+1]
        node = parent.children[idx]
        if node.leaf:
            # append parent key
            node.keys.append(parent.keys[idx])
            # replace parent key with right's first key
            parent.keys[idx] = right.keys.pop(0)
        else:
            node.keys.append(parent.keys[idx])
            child_to_move = right.children.pop(0)
            node.children.append(child_to_move)
            parent.keys[idx] = right.keys.pop(0)
        self.rotations += 1

    # Merge helpers
    def merge_with_left(self, parent, idx):
        # Merge child at idx with left sibling at idx-1
        left = parent.children[idx-1]
        node = parent.children[idx]
        # move parent separator down
        left.keys.append(parent.keys[idx-1])
        # concatenate node
        left.keys.extend(node.keys)
        if not left.leaf:
            left.children.extend(node.children)
        # remove from parent
        parent.keys.pop(idx-1)
        parent.children.pop(idx)
        self.merges += 1
        return left  # merged node reference

    def merge_with_right(self, parent, idx):
        node = parent.children[idx]
        right = parent.children[idx+1]
        # move parent separator down
        node.keys.append(parent.keys[idx])
        node.keys.extend(right.keys)
        if not node.leaf:
            node.children.extend(right.children)
        parent.keys.pop(idx)
        parent.children.pop(idx+1)
        self.merges += 1
        return node

    # Fix underflow after deletions
    def fix_underflow(self, node):
        # If node is root, handle special cases
        while True:
            if node is self.root:
                # If root is internal and empty with one child, collapse
                if not node.leaf and len(node.keys) == 0 and len(node.children) == 1:
                    self.root = node.children[0]
                # Leaf root may be empty if tree emptied; nothing else to do
                return
            parent, idx = self.find_parent(self.root, node)
            if parent is None:
                # Should not happen
                return
            min_keys = (self.t - 1)
            if len(node.keys) >= min_keys:
                return
            deficiency = min_keys - len(node.keys)
            # calculate available surplus from siblings
            # Try left
            if idx - 1 >= 0:
                left = parent.children[idx-1]
                avail_left = len(left.keys) - (self.t - 1)
            else:
                left = None
                avail_left = 0
            # Try right
            if idx + 1  len(parent.children):
                right = parent.children[idx+1]
                avail_right = len(right.keys) - (self.t - 1)
            else:
                right = None
                avail_right = 0
            # Borrow as much as possible
            borrow_left = min(deficiency, max(0, avail_left))
            for _ in range(borrow_left):
                self.rotate_from_left(parent, idx)
                deficiency -= 1
                if deficiency == 0:
                    break
            if deficiency > 0:
                borrow_right = min(deficiency, max(0, avail_right))
                for _ in range(borrow_right):
                    self.rotate_from_right(parent, idx)
                    deficiency -= 1
                    if deficiency == 0:
                        break
            # If still deficient, merge
            if len(node.keys)  min_keys:
                if left is not None:
                    merged = self.merge_with_left(parent, idx)
                    # After merge, set node to parent to check if parent underflows
                    node = parent
                    continue
                elif right is not None:
                    merged = self.merge_with_right(parent, idx)
                    node = parent
                    continue
                else:
                    # No siblings? Should not happen in B-tree except at root
                    node = parent
                    continue
            else:
                return

    # Batch delete k smallest keys from leaf containing pivot key
    def batch_delete_from_leaf_by_pivot(self, pivot_key, k, pre_reduce_siblings_to_min=False):
        # Find leaf containing pivot key
        node, idx = self.search(pivot_key)
        # Ensure node is the leaf containing the key
        # If the found node is internal (unlikely since search returns leaf when not found),
        # traverse to leaf containing pivot_key
        if not node.leaf:
            # descend to the child where pivot would be
            x = node
            i = 0
            while i  len(x.keys) and pivot_key > x.keys[i]:
                i += 1
            node = x.children[i]
            while not node.leaf:
                i = 0
                while i  len(node.keys) and pivot_key > node.keys[i]:
                    i += 1
                node = node.children[i]
        # Optionally pre-reduce siblings to minimum keys
        if pre_reduce_siblings_to_min:
            parent, child_idx = self.find_parent(self.root, node)
            if parent is not None:
                # Reduce left sibling
                if child_idx - 1 >= 0:
                    left = parent.children[child_idx-1]
                    # delete from left to reach t-1 keys
                    excess = len(left.keys) - (self.t - 1)
                    if excess > 0:
                        # delete excess smallest keys in left
                        to_delete = left.keys[:excess]
                        for key in to_delete:
                            # Remove key from left
                            left.keys.remove(key)
                        # Fix underflow if created (shouldn't create underflow by removing surplus)
                        # But if we removed too many due to earlier structure, still fix
                        self.fix_underflow(left)
                # Reduce right sibling
                if child_idx + 1  len(parent.children):
                    right = parent.children[child_idx+1]
                    excess = len(right.keys) - (self.t - 1)
                    if excess > 0:
                        to_delete = right.keys[:excess]
                        for key in to_delete:
                            right.keys.remove(key)
                        self.fix_underflow(right)
        # Now delete k smallest keys in the target leaf
        to_remove = node.keys[:min(k, len(node.keys))]
        for key in to_remove:
            # Remove key
            node.keys.remove(key)
        # Fix potential underflow
        self.fix_underflow(node)

    # Validation of B-tree invariants
    def validate(self):
        if self.root is None:
            return True
        # Check leaf depths
        leaf_depths = []

        def dfs(node, depth):
            # keys sorted
            if any(node.keys[i] >= node.keys[i+1] for i in range(len(node.keys)-1)):
                return False
            # bounds
            if node is self.root:
                # root can be leaf or internal
                if node.leaf:
                    # allow empty root leaf or between 1 and 2t-1 keys
                    if len(node.keys)  0 or len(node.keys) > 2*self.t - 1:
                        return False
                else:
                    if len(node.keys)  1 or len(node.keys) > 2*self.t - 1:
                        return False
            else:
                if len(node.keys)  self.t - 1 or len(node.keys) > 2*self.t - 1:
                    return False
            if not node.leaf:
                if len(node.children) != len(node.keys) + 1:
                    return False
                for child in node.children:
                    if child is None:
                        return False
            else:
                leaf_depths.append(depth)
            # recurse
            if not node.leaf:
                for child in node.children:
                    if not dfs(child, depth+1):
                        return False
            return True

        ok = dfs(self.root, 1)
        if not ok:
            return False
        # All leaves same depth
        if len(leaf_depths) == 0:
            return True
        return all(d == leaf_depths[0] for d in leaf_depths)

    def height(self):
        h = 0
        node = self.root
        while node is not None:
            h += 1
            if node.leaf:
                break
            node = node.children[0]
        return h


def build_btree_with_range(t, start, end):
    bt = BTree(t)
    for k in range(start, end+1):
        bt.insert(k)
    return bt


def run_test_case(t, insert_range, pivot, k, pre_reduce=False):
    bt = build_btree_with_range(t, insert_range[0], insert_range[1])
    # perform batch deletion
    bt.batch_delete_from_leaf_by_pivot(pivot, k, pre_reduce_siblings_to_min=pre_reduce)
    # collect results
    merges = bt.merges
    rotations = bt.rotations
    height = bt.height()
    valid = bt.validate()
    return [merges, rotations, height, valid]


def solve():
    # Define the test cases from the problem statement.
    test_cases = [
        # Case 1: t=3, insert 1..30, pivot 11, k=3, no pre-reduction
        (3, (1, 30), 11, 3, False),
        # Case 2: t=3, insert 1..30, pivot 11, k=4, no pre-reduction
        (3, (1, 30), 11, 4, False),
        # Case 3: t=3, insert 1..30, pivot 11, k=4, pre-reduction enabled
        (3, (1, 30), 11, 4, True),
        # Case 4: t=2, insert 1..10, pivot 6, k=3, pre-reduction enabled
        (2, (1, 10), 6, 3, True),
    ]

    results = []
    for case in test_cases:
        t, insert_range, pivot, k, pre_reduce = case
        result = run_test_case(t, insert_range, pivot, k, pre_reduce)
        results.append(result)

    # Final print statement in the exact required format.
    print(f"[{','.join(map(str, results))}]")

solve()
