import numpy as np
from collections import deque

class Node:
    """A class to represent a node in the treap."""
    def __init__(self, key, priority):
        self.key = int(key)
        self.priority = int(priority)
        self.left = None
        self.right = None

class Treap:
    """A class to represent a treap data structure."""
    def __init__(self):
        self.root = None

    def _rotate_left(self, y):
        """Performs a left rotation on the subtree rooted at y."""
        x = y.right
        T2 = x.left
        x.left = y
        y.right = T2
        return x

    def _rotate_right(self, y):
        """Performs a right rotation on the subtree rooted at y."""
        x = y.left
        T2 = x.right
        x.right = y
        y.left = T2
        return x

    def _insert_recursive(self, node, key, priority):
        """Recursive helper for insertion, maintaining treap properties."""
        if not node:
            return Node(key, priority)

        if key  node.key:
            node.left = self._insert_recursive(node.left, key, priority)
            # If heap property is violated, rotate right
            if node.left.priority  node.priority:
                node = self._rotate_right(node)
        elif key > node.key:
            node.right = self._insert_recursive(node.right, key, priority)
            # If heap property is violated, rotate left
            if node.right.priority  node.priority:
                node = self._rotate_left(node)

        return node

    def insert(self, key, priority):
        """Public method for insertion."""
        self.root = self._insert_recursive(self.root, key, priority)

    def delete(self, key):
        """
        Non-recursive deletion using a stack to manage the path.
        """
        # Step 1: Find the node and store the path to it.
        # The stack stores tuples of (parent_node, is_left_child_bool).
        path_stack = []
        node = self.root

        while node and node.key != key:
            parent = node
            if key  node.key:
                path_stack.append((parent, True))
                node = node.left
            else:
                path_stack.append((parent, False))
                node = node.right

        if not node:
            return False  # Key not found

        # Step 2: "Bubble down" the node using rotations until it has at most one child.
        while node.left and node.right:
            # Decide rotation based on child priorities.
            if node.left.priority  node.right.priority:
                new_sub_root = self._rotate_right(node)
            else:
                new_sub_root = self._rotate_left(node)

            # Update the parent of the rotated subtree.
            if not path_stack:
                self.root = new_sub_root
            else:
                parent, is_left = path_stack[-1]
                if is_left:
                    parent.left = new_sub_root
                else:
                    parent.right = new_sub_root

            # The node to delete has moved down. Update path_stack for the next iteration.
            # a new parent-child relation is established for the node we are deleting.
            path_stack.append((new_sub_root, node is new_sub_root.left))

        # Step 3: Remove the node, which now has at most one child.
        child = node.left if node.left else node.right

        if not path_stack:
            self.root = child
        else:
            parent, is_left = path_stack[-1]
            if is_left:
                parent.left = child
            else:
                parent.right = child

        return True

    def get_in_order_keys(self):
        """Returns a list of keys from an in-order traversal."""
        if not self.root:
            return []

        result = []
        stack = []
        current = self.root
        while current or stack:
            while current:
                stack.append(current)
                current = current.left
            current = stack.pop()
            result.append(current.key)
            current = current.right
        return result

    def check_bst(self):
        """Checks if the tree satisfies the BST property (strictly increasing keys in-order)."""
        keys = self.get_in_order_keys()
        for i in range(1, len(keys)):
            if keys[i] = keys[i-1]:
                return False
        return True

    def check_heap(self):
        """Checks if the tree satisfies the min-heap property on priorities."""
        if not self.root:
            return True

        q = deque([self.root])
        while q:
            node = q.popleft()
            if node.left:
                if node.priority > node.left.priority:
                    return False
                q.append(node.left)
            if node.right:
                if node.priority > node.right.priority:
                    return False
                q.append(node.right)
        return True

def solve():
    test_cases = [
        ({'pairs': {(5,10),(3,20),(8,30)}, 'delete_key': 3}),
        ({'pairs': {(5,10),(3,20),(4,15)}, 'delete_key': 4}),
        ({'pairs': {(5,10),(3,20),(7,15),(6,25)}, 'delete_key': 5}),
        ({'pairs': {(5,10),(3,20),(7,15)}, 'delete_key': 42}),
        ({'pairs': set(), 'delete_key': 1}),
        ({'pairs': {(50,5),(20,15),(70,10),(10,25),(30,40),(60,35),(80,20),(25,30),(35,45)}, 'delete_key': 50}),
    ]

    results = []
    for case in test_cases:
        treap = Treap()
        for key, priority in case['pairs']:
            treap.insert(key, priority)

        deleted = treap.delete(case['delete_key'])
        bst_ok = treap.check_bst()
        heap_ok = treap.check_heap()
        inorder = treap.get_in_order_keys()

        results.append([deleted, bst_ok, heap_ok, inorder])

    # Final print statement in the exact required format.
    # Using str() and replace() to ensure no spaces in the list representation
    # e.g., [1,2] instead of [1, 2]
    print(str(results).replace(" ", ""))

