import numpy as np

def _get_neighbors():
    """Pre-computes and returns the neighbors for each cell in a 9x9 grid."""
    neighbors = {}
    for r in range(9):
        for c in range(9):
            neighbor_set = set()
            # Row neighbors
            for i in range(9):
                if i != c:
                    neighbor_set.add((r, i))
            # Column neighbors
            for i in range(9):
                if i != r:
                    neighbor_set.add((i, c))
            # Box neighbors
            start_row, start_col = 3 * (r // 3), 3 * (c // 3)
            for i in range(start_row, start_row + 3):
                for j in range(start_col, start_col + 3):
                    if (i, j) != (r, c):
                        neighbor_set.add((i, j))
            neighbors[(r, c)] = list(neighbor_set)
    return neighbors

NEIGHBORS = _get_neighbors()

def _propagate(domains, r, c):
    """
    Propagates constraints after assigning a value to (r, c).
    A cell's domain must be a singleton.
    Modifies domains in place. Returns False if a contradiction is found.
    """
    val = next(iter(domains[r][c]))
    q = [(r, c)]
    visited = set(q)

    while q:
        curr_r, curr_c = q.pop(0)
        curr_val = next(iter(domains[curr_r][curr_c]))

        for nr, nc in NEIGHBORS[(curr_r, curr_c)]:
            if curr_val in domains[nr][nc]:
                domains[nr][nc].remove(curr_val)
                if not domains[nr][nc]:
                    return False  # Contradiction
                if len(domains[nr][nc]) == 1 and (nr, nc) not in visited:
                    q.append((nr, nc))
                    visited.add((nr, nc))
    return True

def _backtrack(domains):
    """
    Recursive backtracking solver.
    """
    # Find Most Constrained Variable (MCV)
    mcv_cell = None
    min_domain_size = 10
    unassigned_found = False

    for r in range(9):
        for c in range(9):
            if len(domains[r][c]) > 1:
                unassigned_found = True
                if len(domains[r][c])  min_domain_size:
                    min_domain_size = len(domains[r][c])
                    mcv_cell = (r, c)

    # Base case: if no unassigned cells, solution is found
    if not unassigned_found:
        return domains

    r, c = mcv_cell

    # Order values by Least Constraining Value (LCV)
    lcv_scores = []
    for val in domains[r][c]:
        score = 0
        for nr, nc in NEIGHBORS[(r, c)]:
            if val in domains[nr][nc]:
                score += 1
        lcv_scores.append((score, val))

    lcv_scores.sort()

    # Recursive step
    for _, val in lcv_scores:
        # Create a deep copy of domains for this branch
        new_domains = [[cell_domain.copy() for cell_domain in row] for row in domains]
        new_domains[r][c] = {val}

        # Propagate constraints
        if _propagate(new_domains, r, c):
            result = _backtrack(new_domains)
            if result:
                return result

    return None # Backtrack

def solve_sudoku(puzzle_str):
    """
    Solves a Sudoku puzzle string using backtracking with MCV, LCV, and propagation.
    """
    # 1. Initialize domains
    domains = [[set(range(1, 10)) for _ in range(9)] for _ in range(9)]
    grid = np.array([int(c) for c in puzzle_str]).reshape((9, 9))

    # 2. Initial assignments and propagation
    for r in range(9):
        for c in range(9):
            if grid[r][c] != 0:
                val = grid[r][c]
                if val not in domains[r][c]:
                    return -1 # Initial conflict
                domains[r][c] = {val}
                if not _propagate(domains, r, c):
                    return -1 # Unsolvable from start

    # 3. Call backtracking search
    solution = _backtrack(domains)

    # 4. Format output
    if solution:
        flat_solution = []
        for r in range(9):
            for c in range(9):
                flat_solution.append(list(solution[r][c])[0])
        return flat_solution
    else:
        return -1

def solve():
