import numpy as np

class Payload:
    """A simple payload class to hold a unique identity tag."""
    def __init__(self, identity_tag):
        self.identity_tag = identity_tag

    def __repr__(self):
        return f"Payload({self.identity_tag})"

class Node:
    """A node in the Binary Search Tree."""
    def __init__(self, key, payload):
        self.key = key
        self.payload = payload
        self.left = None
        self.right = None
        self.parent = None

    @property
    def composite_key(self):
        return (self.key, self.payload.identity_tag)

    def __repr__(self):
        return f"Node(k={self.key}, i={self.payload.identity_tag})"

class BST:
    """
    Binary Search Tree implementation handling duplicate integer keys
    via a composite key (k, i) and lexicographic ordering.
    """
    def __init__(self):
        self.root = None

    def insert(self, key, payload):
        """Inserts a new node with the given key and payload."""
        new_node = Node(key, payload)
        y = None
        x = self.root

        while x is not None:
            y = x
            if new_node.composite_key  x.composite_key:
                x = x.left
            else:
                x = x.right

        new_node.parent = y
        if y is None:
            self.root = new_node
        elif new_node.composite_key  y.composite_key:
            y.left = new_node
        else:
            y.right = new_node

    def search(self, key_to_find):
        """
        Returns a list of identity tags for all nodes with the given integer key.
        The results are sorted by identity tag due to in-order traversal.
        """
        results = []
        self._in_order_search(self.root, key_to_find, results)
        return results

    def _in_order_search(self, node, key_to_find, results):
        if node is None:
            return

        if key_to_find  node.key:
            self._in_order_search(node.left, key_to_find, results)
        elif key_to_find > node.key:
            self._in_order_search(node.right, key_to_find, results)
        else: # key_to_find == node.key
            self._in_order_search(node.left, key_to_find, results)
            results.append(node.payload.identity_tag)
            self._in_order_search(node.right, key_to_find, results)

    def _find_node(self, key, payload):
        """Finds a specific node by its composite key and payload identity."""
        node = self.root
        target_composite_key = (key, payload.identity_tag)
        while node is not None and target_composite_key != node.composite_key:
            if target_composite_key  node.composite_key:
                node = node.left
            else:
                node = node.right

        # Additionally verify pointer identity
        if node and node.payload is payload:
            return node
        return None

    def delete(self, key, payload):
        """Deletes the specific node identified by key and payload object."""
        node_to_delete = self._find_node(key, payload)
        if node_to_delete is None:
            return False

        self._delete_node(node_to_delete)
        return True

    def _transplant(self, u, v):
        """Replaces subtree rooted at u with subtree rooted at v."""
        if u.parent is None:
            self.root = v
        elif u == u.parent.left:
            u.parent.left = v
        else:
            u.parent.right = v
        if v is not None:
            v.parent = u.parent

    def _find_min(self, node):
        """Finds the node with the minimum key in a subtree."""
        while node.left is not None:
            node = node.left
        return node

    def _delete_node(self, z):
        """Helper function to perform the deletion of node z."""
        if z.left is None:
            self._transplant(z, z.right)
        elif z.right is None:
            self._transplant(z, z.left)
        else:
            y = self._find_min(z.right)
            if y.parent != z:
                self._transplant(y, y.right)
                y.right = z.right
                y.right.parent = y

            self._transplant(z, y)
            y.left = z.left
            y.left.parent = y

    def get_in_order_traversal(self):
        """Returns two lists: keys and tags from an in-order traversal."""
        keys = []
        tags = []
        self._in_order_collect(self.root, keys, tags)
        return keys, tags

    def _in_order_collect(self, node, keys, tags):
        if node is not None:
            self._in_order_collect(node.left, keys, tags)
            keys.append(node.key)
            tags.append(node.payload.identity_tag)
            self._in_order_collect(node.right, keys, tags)

def solve():
    test_cases = [
        {
            "inserts": [(5, 10), (3, 20), (7, 30), (3, 21), (5, 11), (7, 31)],
            "delete": (5, 11),
            "search": 5
        },
        {
            "inserts": [(4, 40), (2, 50), (6, 60), (6, 61)],
            "delete": (6, 61),
            "search": 6
        },
        {
            "inserts": [(8, 80), (8, 81), (9, 90)],
            "delete": (8, 80),
            "search": 8
        },
        {
            "inserts": [(6, 100), (4, 101), (7, 102), (5, 103), (3, 104), (6, 105)],
            "delete": (6, 100),
            "search": 6
        },
        {
            "inserts": [],
            "delete": (1, 200),
            "search": 1
        },
        {
            "inserts": [(10, 300), (10, 301)],
            "delete": (10, 302),
            "search": 10
        }
    ]

    results = []
    for case in test_cases:
        bst = BST()
        payload_map = {}

        # Perform insertions
        for k, i in case["inserts"]:
            p = Payload(i)
            payload_map[i] = p
            bst.insert(k, p)

        # Perform deletion
        del_key, del_tag = case["delete"]
        payload_to_delete = payload_map.get(del_tag)
        # If the payload wasn't inserted, we create a dummy for the delete attempt
        if payload_to_delete is None:
            payload_to_delete = Payload(del_tag)

        deletion_succeeded = bst.delete(del_key, payload_to_delete)

        # Get post-deletion state
        in_order_keys, in_order_tags = bst.get_in_order_traversal()

        # Perform search
        search_results = bst.search(case["search"])
        search_count = len(search_results)

        results.append([
            in_order_keys,
            in_order_tags,
            search_count,
            deletion_succeeded
        ])

    # Format the output as a single line JSON-like string with no spaces
    print(str(results).replace(" ", ""))

solve()
