import numpy as np

class Node:
    """
    A node in an augmented Binary Search Tree.
    Each node stores its key, pointers to left and right children,
    and the size of the subtree rooted at this node.
    """
    def __init__(self, key):
        self.key = key
        self.left = None
        self.right = None
        self.size = 1

class AugmentedBST:
    """
    An augmented Binary Search Tree that supports order-statistic queries.
    """
    def __init__(self):
        self.root = None

    def _get_size(self, node):
        """Helper to get size of a node's subtree, returns 0 if node is None."""
        return node.size if node else 0

    def insert(self, key):
        """Inserts a key into the BST, ignoring duplicates."""
        self.root = self._insert_recursive(self.root, key)

    def _insert_recursive(self, node, key):
        """Recursively inserts a key and updates subtree sizes."""
        if not node:
            return Node(key)

        if key  node.key:
            node.left = self._insert_recursive(node.left, key)
        elif key > node.key:
            node.right = self._insert_recursive(node.right, key)
        else: # Key already exists, do nothing.
            return node

        # Update the size of the current node
        node.size = 1 + self._get_size(node.left) + self._get_size(node.right)
        return node

    def r(self, k):
        """Returns the number of keys in the tree less than or equal to k."""
        return self._r_recursive(self.root, k)

    def _r_recursive(self, node, k):
        """Recursive helper for rank query."""
        if not node:
            return 0

        if k  node.key:
            return self._r_recursive(node.left, k)
        elif k > node.key:
            return self._get_size(node.left) + 1 + self._r_recursive(node.right, k)
        else: # k == node.key
            return self._get_size(node.left) + 1

    def select(self, t):
        """Returns the key with the t-th in-order rank (1-indexed)."""
        if not self.root or not (1 = t = self.root.size):
            return None # Or raise an error, indicates invalid rank
        return self._select_recursive(self.root, t)

    def _select_recursive(self, node, t):
        """Recursive helper for select query."""
        if not node:
            # This case should not be reached if initial rank is valid.
            return None

        left_size = self._get_size(node.left)
        current_rank = left_size + 1

        if t == current_rank:
            return node.key
        elif t  current_rank:
            return self._select_recursive(node.left, t)
        else: # t > current_rank
            return self._select_recursive(node.right, t - current_rank)

    def generalized_successor(self, k, m):
        """
        Computes the generalized m-th successor of k.
        """
        n = self._get_size(self.root)
        if n == 0:
            return -1

        rank_k = self.r(k)
        target_rank = rank_k + m

        if 1 = target_rank = n:
            return self.select(target_rank)
        else:
            return -1

def solve():
    """
    Main function to run the test cases and print the results.
    """
    test_cases = [
        ({'keys': [20, 10, 30, 5, 15, 25, 35], 'query': (10, 1)}),
        ({'keys': [20, 10, 30, 5, 15, 25, 35], 'query': (10, 0)}),
        ({'keys': [20, 10, 30, 5, 15, 25, 35], 'query': (10, 3)}),
        ({'keys': [20, 10, 30, 5, 15, 25, 35], 'query': (34, 1)}),
        ({'keys': [20, 10, 30, 5, 15, 25, 35], 'query': (35, 1)}),
        ({'keys': [20, 10, 30, 5, 15, 25, 35], 'query': (-100, 1)}),
        ({'keys': [1, 2, 3, 4], 'query': (2, 2)}),
        ({'keys': [1, 2, 3, 4], 'query': (2, 10)}),
        ({'keys': [1, 2, 3, 4], 'query': (100, 0)}),
        ({'keys': [], 'query': (5, 1)}),
    ]

    results = []
    for case in test_cases:
        keys = case['keys']
        k, m = case['query']

        bst = AugmentedBST()
        for key in keys:
            bst.insert(key)

        result = bst.generalized_successor(k, m)
        results.append(result)

    print(f"[{','.join(map(str, results))}]")

solve()
