import numpy as np
import sys

# Increase recursion limit for deep trees; a safeguard for robust tree algorithms.
sys.setrecursionlimit(2000)

class Node:
    """A node in the Red-Black Tree."""
    def __init__(self, key, color='RED', parent=None, left=None, right=None):
        self.key = key
        self.color = color
        self.parent = parent
        self.left = left
        self.right = right

    def __repr__(self):
        return f"Node(key={self.key}, color={self.color})"

class RedBlackTree:
    """A Red-Black Tree implementation supporting key updates."""
    def __init__(self):
        # The NIL sentinel is a single black node representing all leaves.
        self.NIL = Node(key=None, color='BLACK')
        self.root = self.NIL
        self.size = 0

    def _left_rotate(self, x):
        y = x.right
        x.right = y.left
        if y.left is not self.NIL:
            y.left.parent = x
        y.parent = x.parent
        if x.parent is self.NIL:
            self.root = y
        elif x == x.parent.left:
            x.parent.left = y
        else:
            x.parent.right = y
        y.left = x
        x.parent = y

    def _right_rotate(self, y):
        x = y.left
        y.left = x.right
        if x.right is not self.NIL:
            x.right.parent = y
        x.parent = y.parent
        if y.parent is self.NIL:
            self.root = x
        elif y == y.parent.right:
            y.parent.right = x
        else:
            y.parent.left = x
        x.right = y
        y.parent = x

    def insert(self, key):
        if self.find(key) is not self.NIL:
            return

        z = Node(key=key, left=self.NIL, right=self.NIL)
        y = self.NIL
        x = self.root

        while x is not self.NIL:
            y = x
            if z.key < x.key:
                x = x.left
            else:
                x = x.right

        z.parent = y
        if y is self.NIL:
            self.root = z
        elif z.key < y.key:
            y.left = z
        else:
            y.right = z

        z.color = 'RED'
        self._insert_fixup(z)
        self.size += 1

    def _insert_fixup(self, z):
        while z.parent.color == 'RED':
            if z.parent == z.parent.parent.left:
                y = z.parent.parent.right  # Uncle
                if y.color == 'RED':
                    z.parent.color = 'BLACK'
                    y.color = 'BLACK'
                    z.parent.parent.color = 'RED'
                    z = z.parent.parent
                else:
                    if z == z.parent.right:
                        z = z.parent
                        self._left_rotate(z)
                    z.parent.color = 'BLACK'
                    z.parent.parent.color = 'RED'
                    self._right_rotate(z.parent.parent)
            else:
                y = z.parent.parent.left  # Uncle
                if y.color == 'RED':
                    z.parent.color = 'BLACK'
                    y.color = 'BLACK'
                    z.parent.parent.color = 'RED'
                    z = z.parent.parent
                else:
                    if z == z.parent.left:
                        z = z.parent
                        self._right_rotate(z)
                    z.parent.color = 'BLACK'
                    z.parent.parent.color = 'RED'
                    self._left_rotate(z.parent.parent)
        self.root.color = 'BLACK'

    def _transplant(self, u, v):
        if u.parent is self.NIL:
            self.root = v
        elif u == u.parent.left:
            u.parent.left = v
        else:
            u.parent.right = v
        v.parent = u.parent

    def delete(self, key):
        z = self.find(key)
        if z is self.NIL:
            return False

        y = z
        y_original_color = y.color
        if z.left is self.NIL:
            x = z.right
            self._transplant(z, z.right)
        elif z.right is self.NIL:
            x = z.left
            self._transplant(z, z.left)
        else:
            y = self._tree_minimum(z.right)
            y_original_color = y.color
            x = y.right
            if y.parent == z:
                x.parent = y
            else:
                self._transplant(y, y.right)
                y.right = z.right
                y.right.parent = y
            self._transplant(z, y)
            y.left = z.left
            y.left.parent = y
            y.color = z.color

        if y_original_color == 'BLACK':
            self._delete_fixup(x)

        self.size -= 1
        return True

    def _delete_fixup(self, x):
        while x is not self.root and x.color == 'BLACK':
            if x == x.parent.left:
                w = x.parent.right
                if w.color == 'RED':
                    w.color = 'BLACK'
                    x.parent.color = 'RED'
                    self._left_rotate(x.parent)
                    w = x.parent.right
                if w.left.color == 'BLACK' and w.right.color == 'BLACK':
                    w.color = 'RED'
                    x = x.parent
                else:
                    if w.right.color == 'BLACK':
                        w.left.color = 'BLACK'
                        w.color = 'RED'
                        self._right_rotate(w)
                        w = x.parent.right
                    w.color = x.parent.color
                    x.parent.color = 'BLACK'
                    w.right.color = 'BLACK'
                    self._left_rotate(x.parent)
                    x = self.root
            else:
                w = x.parent.left
                if w.color == 'RED':
                    w.color = 'BLACK'
                    x.parent.color = 'RED'
                    self._right_rotate(x.parent)
                    w = x.parent.left
                if w.right.color == 'BLACK' and w.left.color == 'BLACK':
                    w.color = 'RED'
                    x = x.parent
                else:
                    if w.left.color == 'BLACK':
                        w.right.color = 'BLACK'
                        w.color = 'RED'
                        self._left_rotate(w)
                        w = x.parent.left
                    w.color = x.parent.color
                    x.parent.color = 'BLACK'
                    w.left.color = 'BLACK'
                    self._right_rotate(x.parent)
                    x = self.root
        x.color = 'BLACK'

    def find(self, key):
        x = self.root
        while x is not self.NIL and key != x.key:
            if key < x.key:
                x = x.left
            else:
                x = x.right
        return x

    def _tree_minimum(self, x):
        while x.left is not self.NIL:
            x = x.left
        return x

    def update(self, k_old, k_new):
        if k_old == k_new:
            return
        if self.find(k_new) is not self.NIL:
            return
        if self.delete(k_old):
            self.insert(k_new)

    def get_in_order_traversal(self):
        result = []
        def _in_order(node):
            if node is not self.NIL:
                _in_order(node.left)
                result.append(node.key)
                _in_order(node.right)
        _in_order(self.root)
        return result

    def get_height(self):
        if self.root is self.NIL:
            return 0
        def _height(node):
            if node is self.NIL:
                return 0
            return 1 + max(_height(node.left), _height(node.right))
        return _height(self.root)

    def get_final_state(self):
        in_order = self.get_in_order_traversal()

        is_bst = all(in_order[i] < in_order[i+1] for i in range(len(in_order)-1))

        def _validate_rbt(node):
            if node is self.NIL:
                return True, True, 1

            p4_node = True
            if node.color == 'RED' and (node.left.color == 'RED' or node.right.color == 'RED'):
                p4_node = False

            p4_left, p5_left, bh_left = _validate_rbt(node.left)
            p4_right, p5_right, bh_right = _validate_rbt(node.right)

            is_p4_valid = p4_node and p4_left and p4_right
            is_p5_valid = p5_left and p5_right and (bh_left == bh_right)

            bh = -1
            if is_p5_valid:
                bh = bh_left + (1 if node.color == 'BLACK' else 0)

            return is_p4_valid, is_p5_valid, bh

        validity_flag = 0
        black_height = -1

        if self.root is self.NIL:
            validity_flag = 1
            black_height = 1
        elif self.root.color == 'BLACK':
            p4_valid, p5_valid, bh = _validate_rbt(self.root)
            if is_bst and p4_valid and p5_valid:
                validity_flag = 1
                black_height = bh

        return [validity_flag, self.size, black_height, self.get_height(), in_order]

def solve():
    test_cases = [
        {"initial_keys": [10, 20, 30, 15, 25, 5, 1], "updates": [(15, 17), (25, 22), (1, 26)]},
        {"initial_keys": [7, 3, 18, 10, 22, 8, 11, 26], "updates": [(3, 2), (26, 30), (7, 7), (8, 9)]},
        {"initial_keys": [1, 2, 3, 4, 5], "updates": [(6, 0), (2, 3), (5, 5), (4, -1)]},
        {"initial_keys": [50, 25, 75, 12, 37, 62, 87, 6, 18, 31, 43, 56, 68, 81, 93], "updates": [(37, 38), (62, 24), (81, 90), (12, 13), (93, 1)]}
    ]

    results = []
    for case in test_cases:
        tree = RedBlackTree()
        for key in case["initial_keys"]:
            tree.insert(key)

        for k_old, k_new in case["updates"]:
            tree.update(k_old, k_new)

        final_state = tree.get_final_state()
        results.append(final_state)

    print(f"[{','.join(map(str, results))}]")

solve()
