import numpy as np
import collections

def find_shortest_admissible_path(V, E, s, t, F):
    """
    Computes the shortest admissible path from s to t with lexicographical tie-breaking.

    Args:
        V (set): The set of nodes.
        E (set): The set of undirected edges as tuples.
        s (int): The source node.
        t (int): The target node.
        F (set): The set of forbidden nodes.

    Returns:
        list: The sequence of nodes in the path, or an empty list if no path exists.
    """
    # Step 1: Initial checks for admissibility.
    if s in F or t in F:
        return []

    # Handle the boundary condition where s and t are the same.
    if s == t:
        return [s]

    # Build the adjacency list with sorted neighbors for deterministic exploration.
    adj = {node: [] for node in V}
    for u, v in E:
        adj[u].append(v)
        adj[v].append(u)
    for node in adj:
        adj[node].sort()

    # Step 2: BFS Initialization
    # A standard Python list used as a queue with pop(0) is slow.
    # collections.deque is a double-ended queue optimized for appends and pops from either end.
    queue = collections.deque([s])
    visited = {s}
    # Parent map for path reconstruction.
    parents = {s: None}

    # Step 3: BFS Traversal
    path_found = False
    while queue:
        u = queue.popleft()

        if u == t:
            path_found = True
            break

        for v in adj.get(u, []):
            if v not in visited and v not in F:
                visited.add(v)
                parents[v] = u
                queue.append(v)

    # Step 4: Path Reconstruction
    if not path_found:
        return []

    path = []
    curr = t
    while curr is not None:
        path.append(curr)
        curr = parents[curr]

    return path[::-1] # Reverse the path to get s to t order.

def solve():
    """
    Defines and runs the test cases specified in the problem statement.
    """
    test_cases = [
        {
            "V": {0, 1, 2, 3, 4, 5, 6},
            "E": {(0, 1), (0, 3), (1, 2), (2, 3), (2, 5), (5, 6), (3, 4), (4, 6)},
            "s": 0, "t": 6, "F": {3}
        },
        {
            "V": {0, 1, 2},
            "E": {(0, 1), (1, 2)},
            "s": 2, "t": 2, "F": set()
        },
        {
            "V": {0, 1, 2, 3, 4},
            "E": {(0, 1), (1, 4), (0, 2), (2, 3), (3, 4)},
            "s": 0, "t": 4, "F": {4}
        },
        {
            "V": {0, 1, 2, 3},
            "E": {(0, 1), (1, 2), (2, 3)},
            "s": 1, "t": 3, "F": {1}
        },
        {
            "V": {0, 1, 2, 3, 4},
            "E": {(0, 1), (1, 4), (0, 2), (2, 4), (0, 3), (3, 4)},
            "s": 0, "t": 4, "F": set()
        }
    ]

    results = []
    for case in test_cases:
        result = find_shortest_admissible_path(
            case["V"], case["E"], case["s"], case["t"], case["F"]
        )
        results.append(result)

    # Custom formatter to match the output specification (no spaces).
    def format_list(lst):
        return f"[{','.join(map(str, lst))}]"

    formatted_results = [format_list(res) for res in results]

    # Final print statement in the exact required format.
    print(f"[{','.join(formatted_results)}]")


solve()
