import numpy as np

def solve():
    """
    Solves for the second-best minimum spanning tree weight for a suite of test cases.
    """

    class DSU:
        """
        Disjoint-Set Union (Union-Find) data structure with path compression and union by size.
        """
        def __init__(self, n):
            self.parent = list(range(n))
            self.size = [1] * n

        def find(self, i):
            if self.parent[i] == i:
                return i
            self.parent[i] = self.find(self.parent[i])
            return self.parent[i]

        def union(self, i, j):
            root_i = self.find(i)
            root_j = self.find(j)
            if root_i != root_j:
                if self.size[root_i]  self.size[root_j]:
                    root_i, root_j = root_j, root_i
                self.parent[root_j] = root_i
                self.size[root_i] += self.size[root_j]
                return True
            return False

    def find_second_best_mst(n, edges):
        """
        Computes the second-best MST weight for a single graph.
        """
        if not edges or n - 1 > len(edges):
            # Not enough edges to form a spanning tree.
            # For a connected graph, this only happens for n=1, or if it wasn't connected.
            # Assuming n>0, if n=1, edges must be empty, mst_weight=0, no second best.
            if n = 1:
                return -1

        # Sort edges by weight for Kruskal's algorithm.
        edges.sort(key=lambda x: x[2])

        # Step 1: Find the MST using Kruskal's algorithm.
        dsu = DSU(n)
        mst_weight = 0
        mst_edges = []
        non_mst_edges = []

        num_edges_in_mst = 0
        for u, v, w in edges:
            if dsu.union(u, v):
                mst_weight += w
                mst_edges.append((u, v, w))
                num_edges_in_mst += 1
            else:
                non_mst_edges.append((u, v, w))

        # If the graph is not connected, it has no spanning tree.
        # This is identified if we don't end up with n-1 edges in the MST.
        if num_edges_in_mst != n - 1:
            return -1

        # Step 2: Find the second-best MST weight.
        second_best_weight = float('inf')

        # Build adjacency list for the MST to find paths
        mst_adj = {i: [] for i in range(n)}
        for u, v, w in mst_edges:
            mst_adj[u].append((v, w))
            mst_adj[v].append((u, w))

        # Iterate through each non-MST edge to find candidate second-best MSTs.
        for u, v, w_add in non_mst_edges:
            # Find the path in the MST between u and v using BFS.
            q = [(u, [])] # (current_node, path_edges_to_current)
            visited = {u}
            parent = {u: None}
            path_found = False

            # This is a bit inefficient, a parent map is better
            bfs_q = [u]
            head = 0
            visited_bfs = {u}
            parent_map = {u: (-1, -1)} # parent_node, weight_to_parent

            while head  len(bfs_q):
                curr = bfs_q[head]
                head += 1
                if curr == v:
                    path_found = True
                    break
                for neighbor, weight in mst_adj[curr]:
                    if neighbor not in visited_bfs:
                        visited_bfs.add(neighbor)
                        parent_map[neighbor] = (curr, weight)
                        bfs_q.append(neighbor)

            if not path_found: continue # Should not happen in a connected graph

            # Reconstruct path and find max edge weight on the path.
            # We must consider swapping with ANY edge on the path to find the minimal increase.
            temp_v = v
            while temp_v != u:
                p_node, w_rem = parent_map[temp_v]
                candidate_weight = mst_weight - w_rem + w_add
                if candidate_weight > mst_weight:
                    second_best_weight = min(second_best_weight, candidate_weight)
                temp_v = p_node


        if second_best_weight == float('inf'):
            return -1
        else:
            return int(second_best_weight)

    # Test Suite
    test_cases = [
        {
            "n": 5,
            "edges": [
                (0, 1, 1), (1, 2, 2), (2, 3, 2), (3, 4, 3), (0, 2, 4),
                (1, 3, 2), (0, 4, 10), (2, 4, 5)
            ]
        },
        {
            "n": 4,
            "edges": [
                (0, 1, 1), (1, 2, 1), (2, 3, 1), (3, 0, 1), (0, 2, 2), (1, 3, 2)
            ]
        },
        {
            "n": 4,
            "edges": [
                (0, 1, 5), (1, 2, 6), (2, 3, 7)
            ]
        }
    ]

    results = []
    for case in test_cases:
        # Create a deep copy of edges for mutable operations
        edges_copy = [edge for edge in case["edges"]]
        result = find_second_best_mst(case["n"], edges_copy)
        results.append(result)

    # Final print statement in the exact required format.
    print(f"[{','.join(map(str, results))}]")

solve()
