import numpy as np

def hilbert(n: int) - np.ndarray:
    i = np.arange(n).reshape(-1, 1)
    j = np.arange(n).reshape(1, -1)
    return 1.0 / (i + j + 1.0)

def make_spd_via_Q(n: int, alpha: float, seed: int) - np.ndarray:
    rng = np.random.default_rng(seed)
    Q = rng.uniform(-1.0, 1.0, size=(n, n))
    A = Q.T @ Q + alpha * np.eye(n)
    return A

def make_rank_def_spd(n: int, r: int, seed: int) - np.ndarray:
    rng = np.random.default_rng(seed)
    B = rng.uniform(-1.0, 1.0, size=(r, n))
    A = B.T @ B
    return A

def make_spd_with_eigs(n: int, seed: int) - np.ndarray:
    # Create orthonormal U via QR of a Gaussian matrix
    rng = np.random.default_rng(seed)
    G = rng.normal(0.0, 1.0, size=(n, n))
    Q, _ = np.linalg.qr(G)
    # Prescribed eigenvalues: log10 spaced from -8 to 0
    exponents = -8.0 + 8.0 * (np.arange(n) / (n - 1))
    eigs = 10.0 ** exponents
    A = Q @ np.diag(eigs) @ Q.T
    return A

def amplification_factor(x_est: np.ndarray, x_true: np.ndarray, noise: np.ndarray) - float:
    num = np.linalg.norm(x_est - x_true, ord=2)
    den = np.linalg.norm(noise, ord=2)
    # Avoid division by zero; if no noise, define amplification as +inf
    if den == 0.0:
        return float('inf')
    return num / den

def safe_inv_solve(A: np.ndarray, b: np.ndarray):
    # Returns solution via explicit inverse, or raises/returns inf if fails
    try:
        A_inv = np.linalg.inv(A)
        return A_inv @ b
    except Exception:
        return None

def safe_direct_solve(A: np.ndarray, b: np.ndarray):
    try:
        return np.linalg.solve(A, b)
    except Exception:
        return None

def compute_case(case):
    case_type = case['type']
    if case_type == 'spd_q':
        A = make_spd_via_Q(case['n'], case['alpha'], case['seed_A'])
    elif case_type == 'hilbert':
        A = hilbert(case['n'])
    elif case_type == 'rank_def_spd':
        A = make_rank_def_spd(case['n'], case['rank'], case['seed_A'])
    elif case_type == 'spd_eigs':
        A = make_spd_with_eigs(case['n'], case['seed_A'])
    else:
        raise ValueError("Unknown case type")

    n = case['n']
    # Ground truth and noise
    rng_x = np.random.default_rng(case['seed_x'])
    x_true = rng_x.uniform(-1.0, 1.0, size=n)
    b_clean = A @ x_true

    rng_noise = np.random.default_rng(case['seed_noise'])
    noise = rng_noise.normal(0.0, case['noise_std'], size=n)
    b_noisy = b_clean + noise

    # Condition numbers
    try:
        cond_A = np.linalg.cond(A)  # 2-norm condition number
    except Exception:
        cond_A = float('inf')

    A_shift = A + case['lambda'] * np.eye(n)
    try:
        cond_A_shift = np.linalg.cond(A_shift)
    except Exception:
        cond_A_shift = float('inf')

    # Solutions
    x_inv_noisy = safe_inv_solve(A, b_noisy)
    x_solve_noisy = safe_direct_solve(A, b_noisy)
    x_shift_noisy = safe_direct_solve(A_shift, b_noisy)

    # Clean RHS for algorithmic discrepancy
    x_inv_clean = safe_inv_solve(A, b_clean)
    x_solve_clean = safe_direct_solve(A, b_clean)

    # Amplification factors
    if x_inv_noisy is None:
        g_inv = float('inf')
    else:
        g_inv = amplification_factor(x_inv_noisy, x_true, noise)

    if x_solve_noisy is None:
        g_solve = float('inf')
    else:
        g_solve = amplification_factor(x_solve_noisy, x_true, noise)

    if x_shift_noisy is None:
        g_shift = float('inf')
    else:
        g_shift = amplification_factor(x_shift_noisy, x_true, noise)

    # Algorithmic discrepancy
    if (x_inv_clean is None) or (x_solve_clean is None):
        delta_inv = float('inf')
    else:
        denom = np.linalg.norm(x_solve_clean, ord=2)
        if denom == 0.0:
            delta_inv = float('inf')
        else:
            delta_inv = np.linalg.norm(x_inv_clean - x_solve_clean, ord=2) / denom

    is_superior = g_shift  min(g_solve, g_inv)

    return [g_inv, g_solve, g_shift, cond_A, cond_A_shift, delta_inv, is_superior]

def solve():
    test_cases = [
        {
            'type': 'spd_q',
            'n': 10,
            'alpha': 1e-2,
            'seed_A': 7,
            'seed_x': 11,
            'seed_noise': 13,
            'noise_std': 1e-6,
            'lambda': 1e-1,
        },
        {
            'type': 'hilbert',
            'n': 12,
            'seed_A': None,  # Deterministic
            'seed_x': 17,
            'seed_noise': 19,
            'noise_std': 1e-4,
            'lambda': 1e-1,
        },
        {
            'type': 'rank_def_spd',
            'n': 10,
            'rank': 8,
            'seed_A': 23,
            'seed_x': 29,
            'seed_noise': 31,
            'noise_std': 1e-6,
            'lambda': 1.0,
        },
        {
            'type': 'spd_eigs',
            'n': 12,
            'seed_A': 37,
            'seed_x': 41,
            'seed_noise': 43,
            'noise_std': 1e-5,
            'lambda': 1e-2,
        },
    ]

    results = []
    for case in test_cases:
        results.append(compute_case(case))

    # Print single-line, bracket-enclosed, comma-separated list of per-case lists
    # Ensure booleans and floats are printed via default str
    def format_item(item):
        if isinstance(item, list):
            return "[" + ",".join(format_item(x) for x in item) + "]"
        else:
            return str(item)

    print("[" + ",".join(format_item(r) for r in results) + "]")

if __name__ == "__main__":
    solve()
