import numpy as np

# A consistent execution environment requires no user input and all dependencies declared.

# Define unary functions that can operate on Node objects or raw numbers
def sin(node):
    """Computes sine, handling both Node objects and numeric types."""
    if isinstance(node, Node):
        return node.graph.sin(node)
    return np.sin(node)

def exp(node):
    """Computes exponential, handling both Node objects and numeric types."""
    if isinstance(node, Node):
        return node.graph.exp(node)
    return np.exp(node)

def log(node):
    """Computes natural logarithm, handling both Node objects and numeric types."""
    if isinstance(node, Node):
        return node.graph.log(node)
    return np.log(node)

class Graph:
    """Manages the computational graph, tape, and differentiation process."""
    def __init__(self):
        # A linearized list of operations representing the computational graph.
        # Each entry is a tuple: (op_type, [input_node_ids], output_node_id)
        self.tape = []
        # Stores the numerical value of each node computed during the forward pass.
        self.values = []
        # Stores the adjoint (dL/dv) for each node, computed during the reverse pass.
        self.adjoints = None

    def _add_node(self, value):
        """Adds a new node (value) to the graph and returns its ID."""
        node_id = len(self.values)
        self.values.append(value)
        return node_id

    def variable(self, value):
        """Creates a variable node, which is a leaf in the graph."""
        node_id = self._add_node(value)
        return Node(self, node_id)

    def _promote_to_node(self, other):
        """Promotes a numeric constant to a Node to allow operations like `x + 5`."""
        if not isinstance(other, Node):
            # Treat numeric constants as new variable nodes in the graph.
            return self.variable(other)
        return other

    # Methods for primitive operations (Forward Pass)
    def add(self, n1, n2):
        res_val = self.values[n1.node_id] + self.values[n2.node_id]
        res_id = self._add_node(res_val)
        self.tape.append(('add', [n1.node_id, n2.node_id], res_id))
        return Node(self, res_id)

    def sub(self, n1, n2):
        res_val = self.values[n1.node_id] - self.values[n2.node_id]
        res_id = self._add_node(res_val)
        self.tape.append(('sub', [n1.node_id, n2.node_id], res_id))
        return Node(self, res_id)

    def mul(self, n1, n2):
        res_val = self.values[n1.node_id] * self.values[n2.node_id]
        res_id = self._add_node(res_val)
        self.tape.append(('mul', [n1.node_id, n2.node_id], res_id))
        return Node(self, res_id)

    def truediv(self, n1, n2):
        res_val = self.values[n1.node_id] / self.values[n2.node_id]
        res_id = self._add_node(res_val)
        self.tape.append(('div', [n1.node_id, n2.node_id], res_id))
        return Node(self, res_id)

    def sin(self, n1):
        res_val = np.sin(self.values[n1.node_id])
        res_id = self._add_node(res_val)
        self.tape.append(('sin', [n1.node_id], res_id))
        return Node(self, res_id)

    def exp(self, n1):
        res_val = np.exp(self.values[n1.node_id])
        res_id = self._add_node(res_val)
        self.tape.append(('exp', [n1.node_id], res_id))
        return Node(self, res_id)

    def log(self, n1):
        value = self.values[n1.node_id]
        if value = 0:
            raise ValueError("Domain error: input to log must be positive.")
        res_val = np.log(value)
        res_id = self._add_node(res_val)
        self.tape.append(('log', [n1.node_id], res_id))
        return Node(self, res_id)

    def compute_gradients(self, loss_node):
        """Performs the reverse pass to compute gradients for all nodes."""
        num_nodes = len(self.values)
        self.adjoints = np.zeros(num_nodes)
        self.adjoints[loss_node.node_id] = 1.0  # Seed the reverse pass

        # Replay the tape in reverse to propagate adjoints
        for op_type, input_ids, output_id in reversed(self.tape):
            adjoint_out = self.adjoints[output_id]

            if adjoint_out == 0.0:  # Optimization: no gradient to propagate
                continue

            # Apply the chain rule based on the operation
            if op_type == 'add':
                self.adjoints[input_ids[0]] += adjoint_out
                self.adjoints[input_ids[1]] += adjoint_out
            elif op_type == 'sub':
                self.adjoints[input_ids[0]] += adjoint_out
                self.adjoints[input_ids[1]] -= adjoint_out
            elif op_type == 'mul':
                val0 = self.values[input_ids[0]]
                val1 = self.values[input_ids[1]]
                self.adjoints[input_ids[0]] += adjoint_out * val1
                self.adjoints[input_ids[1]] += adjoint_out * val0
            elif op_type == 'div':
                val0 = self.values[input_ids[0]]
                val1 = self.values[input_ids[1]]
                self.adjoints[input_ids[0]] += adjoint_out / val1
                self.adjoints[input_ids[1]] -= adjoint_out * val0 / (val1**2)
            elif op_type == 'sin':
                val0 = self.values[input_ids[0]]
                self.adjoints[input_ids[0]] += adjoint_out * np.cos(val0)
            elif op_type == 'exp':
                out_val = self.values[output_id] # d/dx(exp(x)) = exp(x)
                self.adjoints[input_ids[0]] += adjoint_out * out_val
            elif op_type == 'log':
                val0 = self.values[input_ids[0]]
                self.adjoints[input_ids[0]] += adjoint_out / val0

class Node:
    """A node in the computational graph, with overloaded operators."""
    def __init__(self, graph, node_id):
        self.graph = graph
        self.node_id = node_id

    @property
    def value(self):
        """Get the node's numerical value from its graph."""
        return self.graph.values[self.node_id]

    @property
    def adjoint(self):
        """Get the node's adjoint after the reverse pass."""
        if self.graph.adjoints is None:
            raise RuntimeError("Gradients not computed yet. Call `compute_gradients` on the loss node first.")
        return self.graph.adjoints[self.node_id]

    # Left-side binary operators
    def __add__(self, other):
        other = self.graph._promote_to_node(other)
        return self.graph.add(self, other)

    def __sub__(self, other):
        other = self.graph._promote_to_node(other)
        return self.graph.sub(self, other)

    def __mul__(self, other):
        other = self.graph._promote_to_node(other)
        return self.graph.mul(self, other)

    def __truediv__(self, other):
        other = self.graph._promote_to_node(other)
        return self.graph.truediv(self, other)

    # Right-side binary operators (for expressions like `5 + x`)
    def __radd__(self, other):
        other = self.graph._promote_to_node(other)
        return self.graph.add(other, self)

    def __rsub__(self, other):
        other = self.graph._promote_to_node(other)
        return self.graph.sub(other, self)

    def __rmul__(self, other):
        other = self.graph._promote_to_node(other)
        return self.graph.mul(other, self)

    def __rtruediv__(self, other):
        other = self.graph._promote_to_node(other)
        return self.graph.truediv(other, self)

    def __repr__(self):
        return f"Node(id={self.node_id}, value={self.value:.4f})"

def solve():
    # Define the test cases from the problem statement.
    test_cases = [
        # Case 1: L = sin(x*y) + exp(y), x=0.5, y=-1.0
        {'vars': {'x': 0.5, 'y': -1.0}, 'expr': lambda x, y: sin(x * y) + exp(y)},
        # Case 2: L = x*0 + sin(0) + log(1), x=2.0
        {'vars': {'x': 2.0}, 'expr': lambda x: x * 0.0 + sin(0.0) + log(1.0)},
        # Case 3: L = (x*x)*x, x=2.0
        {'vars': {'x': 2.0}, 'expr': lambda x: (x * x) * x},
        # Case 4: L = x/y + log(y), x=1.0, y=1.5
        {'vars': {'x': 1.0, 'y': 1.5}, 'expr': lambda x, y: x / y + log(y)},
        # Case 5: L = exp(sin(x)), x=0.0
        {'vars': {'x': 0.0}, 'expr': lambda x: exp(sin(x))},
    ]

    all_results = []
    for case in test_cases:
        g = Graph()
        # Create variable nodes in the specified order (determined by dict insertion order in Python 3.7+)
        var_nodes = {name: g.variable(val) for name, val in case['vars'].items()}

        # Build the graph by executing the expression
        loss_node = case['expr'](**var_nodes)

        # Compute gradients via reverse-mode AD
        g.compute_gradients(loss_node)

        # Collect results: [L, dL/dx1, dL/dx2, ...]
        case_result = [loss_node.value]
        for node in var_nodes.values():
            case_result.append(node.adjoint)

        all_results.append(f"[{','.join(map(str, case_result))}]")

    # Final print statement in the exact required format.
    print(f"[{','.join(all_results)}]")

solve()

