import numpy as np

def composite_trapezoid(f, a, b, N):
    h = (b - a) / N
    x = np.linspace(a, b, N + 1)
    fx = f(x)
    return h * (0.5 * fx[0] + fx[1:-1].sum() + 0.5 * fx[-1])

def composite_simpson(f, a, b, N):
    if N % 2 != 0:
        raise ValueError("Simpson's rule requires an even N.")
    h = (b - a) / N
    x = np.linspace(a, b, N + 1)
    fx = f(x)
    # odd indices 1..N-1 step 2, even indices 2..N-2 step 2
    odd_sum = fx[1:-1:2].sum()
    even_sum = fx[2:-1:2].sum()
    return (h / 3.0) * (fx[0] + fx[-1] + 4.0 * odd_sum + 2.0 * even_sum)

def objective_quadrature(a_param, rule, N):
    # integrand: (a x^2 - sin(2π x))^2
    def integrand(x):
        return (a_param * x**2 - np.sin(2.0 * np.pi * x))**2
    if rule == "trap":
        return composite_trapezoid(integrand, 0.0, 1.0, N)
    elif rule == "simp":
        return composite_simpson(integrand, 0.0, 1.0, N)
    else:
        raise ValueError("Unknown rule")

def finite_diff_grad(a_param, rule, N, delta):
    # centered finite difference
    jp = objective_quadrature(a_param + delta, rule, N)
    jm = objective_quadrature(a_param - delta, rule, N)
    return (jp - jm) / (2.0 * delta)

def gradient_descent(rule, N, eta, delta,
                     a0=0.0, tol_g=1e-8, tol_a=1e-9, max_iter=10000, blowup=1e6):
    a = a0
    converged = False
    iters = 0
    for k in range(max_iter):
        g = finite_diff_grad(a, rule, N, delta)
        # update
        a_new = a - eta * g
        iters = k + 1
        if not np.isfinite(a_new) or abs(a_new)  blowup:
            converged = False
            a = a_new
            break
        # termination checks
        if abs(g)  tol_g or abs(a_new - a)  tol_a:
            converged = True
            a = a_new
            break
        a = a_new
    # If loop ends without break by convergence, check last state
    return converged, iters, a

def main():
    # Analytical minimizer a* = -5 / (2π)
    a_star = -5.0 / (2.0 * np.pi)

    # Test cases: (rule, N, eta, delta)
    test_cases = [
        ("trap", 16, 0.5, 1e-4),
        ("trap", 64, 0.5, 1e-4),
        ("simp", 16, 0.5, 1e-4),
        ("trap", 4, 0.5, 1e-4),
        ("trap", 16, 1.6, 1e-4),
    ]

    results = []
    for rule, N, eta, delta in test_cases:
        # Ensure Simpson has even N (already ensured in the suite)
        try:
            converged, iters, a_final = gradient_descent(rule, N, eta, delta)
        except Exception:
            converged, iters, a_final = (False, 0, float("nan"))
        abs_err = abs(a_final - a_star) if np.isfinite(a_final) else float("inf")
        # Prepare formatted result: [converged(int), iterations(int), a_final(6dp), abs_error(6dp)]
        results.append((1 if converged else 0, iters, a_final, abs_err))

    # Format output exactly as specified: list of lists on one line, comma-separated, no extra text
    out_items = []
    for conv, iters, a_fin, err in results:
        # Round floats to 6 decimals; if not finite, print "nan" or "inf" consistently
        if np.isfinite(a_fin):
            a_str = f"{a_fin:.6f}"
        else:
            a_str = "nan" if np.isnan(a_fin) else "inf"
        if np.isfinite(err):
            e_str = f"{err:.6f}"
        else:
            e_str = "nan" if np.isnan(err) else "inf"
        out_items.append(f"[{conv},{iters},{a_str},{e_str}]")
    print("[" + ",".join(out_items) + "]")

if __name__ == "__main__":
    main()
