import numpy as np

class Dual:
    """
    Dual number for forward-mode automatic differentiation with respect to a single scalar variable.
    Each instance represents a pair (value, derivative).
    """
    __slots__ = ("val", "der")

    def __init__(self, val, der=0.0):
        self.val = float(val)
        self.der = float(der)

    @staticmethod
    def _coerce(other):
        if isinstance(other, Dual):
            return other
        else:
            return Dual(other, 0.0)

    # Addition
    def __add__(self, other):
        o = Dual._coerce(other)
        return Dual(self.val + o.val, self.der + o.der)

    def __radd__(self, other):
        return self.__add__(other)

    # Subtraction
    def __sub__(self, other):
        o = Dual._coerce(other)
        return Dual(self.val - o.val, self.der - o.der)

    def __rsub__(self, other):
        o = Dual._coerce(other)
        return Dual(o.val - self.val, o.der - self.der)

    # Multiplication
    def __mul__(self, other):
        o = Dual._coerce(other)
        # (u, u') * (v, v') = (uv, u'v + uv')
        return Dual(self.val * o.val, self.der * o.val + self.val * o.der)

    def __rmul__(self, other):
        return self.__mul__(other)

    # True division
    def __truediv__(self, other):
        o = Dual._coerce(other)
        # (u, u') / (v, v') = (u/v, (u'v - uv')/v^2)
        denom = o.val * o.val
        return Dual(self.val / o.val, (self.der * o.val - self.val * o.der) / denom)

    def __rtruediv__(self, other):
        o = Dual._coerce(other)
        # o / self
        denom = self.val * self.val
        return Dual(o.val / self.val, (o.der * self.val - o.val * self.der) / denom)

    # Unary negation
    def __neg__(self):
        return Dual(-self.val, -self.der)

    # Power: support real (int/float) exponents, commonly used for integer exponents in polynomials
    def __pow__(self, power):
        if isinstance(power, (int, float)):
            if power == 0:
                # x**0 = 1, derivative 0
                return Dual(1.0, 0.0)
            # For real power p: d(x**p) = p * x**(p - 1) * dx
            primal = self.val ** power
            deriv = power * (self.val ** (power - 1.0)) * self.der
            return Dual(primal, deriv)
        else:
            raise TypeError("Power must be a real number for Dual.__pow__")

def poly(x):
    # f(x) = 3x^5 - 2x^3 + 7x - 11
    return 3 * (x ** 5) - 2 * (x ** 3) + 7 * x - 11

def poly_float(x):
    return 3.0 * (x ** 5) - 2.0 * (x ** 3) + 7.0 * x - 11.0

def poly_derivative_float(x):
    # f'(x) = 15x^4 - 6x^2 + 7
    return 15.0 * (x ** 4) - 6.0 * (x ** 2) + 7.0

def solve():
    # Define the test cases from the problem statement.
    test_cases = [0.0, 1.0, -1.0, 2.5, 10.0]

    results = []
    for x in test_cases:
        # Seed the independent variable: derivative w.r.t. x is 1
        dx = Dual(x, 1.0)
        y = poly(dx)              # Dual result: (value, derivative)
        f_val = poly_float(x)     # Float value
        f_der = poly_derivative_float(x)  # Analytical derivative

        val_err = abs(y.val - f_val)
        der_err = abs(y.der - f_der)

        results.append([val_err, der_err])

    # Final print statement in the exact required format: a single line list of [val_err, der_err] pairs.
    # Format with reasonable precision for readability.
    formatted = "[" + ",".join(f"[{val:.12g},{der:.12g}]" for val, der in results) + "]"
    print(formatted)

if __name__ == "__main__":
    solve()
