import numpy as np

# ---- Automatic Differentiation: Dual Number Implementation ----
class Dual:
    """
    A class for forward-mode automatic differentiation.
    A Dual number z = val + der * epsilon, where epsilon^2 = 0.
    """
    def __init__(self, val, der=0.0):
        self.val = val
        self.der = der

    def __add__(self, other):
        if isinstance(other, Dual):
            return Dual(self.val + other.val, self.der + other.der)
        return Dual(self.val + other, self.der)

    def __radd__(self, other):
        return self.__add__(other)

    def __sub__(self, other):
        if isinstance(other, Dual):
            return Dual(self.val - other.val, self.der - other.der)
        return Dual(self.val - other, self.der)

    def __rsub__(self, other):
        return Dual(other - self.val, -self.der)

    def __mul__(self, other):
        if isinstance(other, Dual):
            return Dual(self.val * other.val, self.der * other.val + self.val * other.der)
        return Dual(self.val * other, self.der * other)

    def __rmul__(self, other):
        return self.__mul__(other)

    def __truediv__(self, other):
        if isinstance(other, Dual):
            val = self.val / other.val
            der = (self.der * other.val - self.val * other.der) / (other.val**2)
            return Dual(val, der)
        return Dual(self.val / other, self.der / other)

    def __rtruediv__(self, other):
        val = other / self.val
        der = (-other * self.der) / (self.val**2)
        return Dual(val, der)

    def __pow__(self, power):
        val = self.val ** power
        der = power * (self.val ** (power - 1)) * self.der
        return Dual(val, der)

    def __neg__(self):
        return Dual(-self.val, -self.der)

    def __repr__(self):
        return f"Dual(val={self.val}, der={self.der})"

def dual_exp(d):
    if isinstance(d, Dual):
        val = np.exp(d.val)
        return Dual(val, val * d.der)
    return np.exp(d)

def dual_pow(base, exp):
    if isinstance(base, Dual):
        return base ** exp
    return base ** exp


# ---- Model and Numerical Method Implementation ----

def chebyshev_poly_vals(x, n):
    """Evaluates the first n Chebyshev polynomials T_0, ..., T_{n-1} at x."""
    if n == 0:
        return []

    T = [1.0, x] if not isinstance(x, Dual) else [Dual(1.0), x]

    for _ in range(n - 2):
        T.append(2.0 * x * T[-1] - T[-2])
    return T[:n]

def evaluate_policy(k, coeffs, params):
    """
    Given capital k and coefficients a, computes c and k'.
    This function handles both float and Dual types.
    """
    alpha, _, delta, _, A, k_min, k_max, n = params

    is_dual = isinstance(coeffs[0], Dual) or isinstance(k, Dual)

    # 1. Map k to x in [-1, 1]
    x = (2.0 * k - (k_min + k_max)) / (k_max - k_min)

    # 2. Evaluate Chebyshev basis
    T_vals = chebyshev_poly_vals(x, n)

    # 3. Compute logit of savings share phi = sum(a_j * T_j(x))
    phi = Dual(0.0) if is_dual else 0.0
    for i in range(n):
        phi += coeffs[i] * T_vals[i]

    # 4. Compute savings share s = 1 / (1 + exp(-phi))
    s = 1.0 / (1.0 + dual_exp(-phi))

    # 5. Compute resources R(k), next-period capital k', and consumption c
    R_k = A * dual_pow(k, alpha) + (1.0 - delta) * k
    k_prime = s * R_k
    c = (1.0 - s) * R_k

    return c, k_prime

def calculate_residuals(coeffs, k_nodes, params):
    """
    Calculates the Euler equation residuals at the given capital nodes.
    Handles both float and Dual coefficients.
    """
    alpha, beta, delta, gamma, A, _, _, _ = params
    is_dual_input = isinstance(coeffs[0], Dual)
    residuals = []

    for k_node in k_nodes:
        # Get c and k' at the current node k
        c, k_prime = evaluate_policy(k_node, coeffs, params)

        # Get c' at the future capital level k'
        c_prime, _ = evaluate_policy(k_prime, coeffs, params)

        # Marginal utility and marginal product
        u_prime_c = dual_pow(c, -gamma)
        u_prime_c_prime = dual_pow(c_prime, -gamma)
        f_prime_k_prime = alpha * A * dual_pow(k_prime, alpha - 1)

        # Euler residual
        res = u_prime_c - beta * u_prime_c_prime * (f_prime_k_prime + 1.0 - delta)
        residuals.append(res)

    if not is_dual_input:
        return np.array([r for r in residuals])
    return residuals

def solve_for_case(case_params):
    """
    Solves the model for a single set of parameters.
    """
    alpha, beta, delta, gamma, A = case_params
    n, M, tol, max_iter = 7, 80, 1e-8, 50

    # 1. Steady state capital
    k_ss_numerator = alpha * A
    k_ss_denominator = 1.0 / beta - 1.0 + delta
    k_ss = (k_ss_numerator / k_ss_denominator) ** (1.0 / (1.0 - alpha))

    # 2. Grid and state space
    k_min, k_max = 0.7 * k_ss, 1.3 * k_ss
    params = (alpha, beta, delta, gamma, A, k_min, k_max, n)

    # Collocation nodes (zeros of T_n)
    chebyshev_zeros = np.cos((2 * np.arange(1, n + 1) - 1) * np.pi / (2 * n))
    k_nodes = (k_min + k_max) / 2.0 + (k_max - k_min) / 2.0 * chebyshev_zeros

    # 3. Initial guess for coefficients 'a'
    R_k_ss = A * k_ss**alpha + (1.0 - delta) * k_ss
    s_ss = k_ss / R_k_ss
    a = np.zeros(n)
    a[0] = np.log(s_ss / (1.0 - s_ss))

    # 4. Newton's method solver
    for i in range(max_iter):
        # Calculate residuals R(a)
        R_val = calculate_residuals(a, k_nodes, params)

        res_norm = np.linalg.norm(R_val, np.inf)
        if res_norm < tol:
            break

        # Calculate Jacobian J(a) using Automatic Differentiation
        J = np.zeros((n, n))
        for j in range(n):
            a_dual = [Dual(val, 1.0 if idx == j else 0.0) for idx, val in enumerate(a)]
            R_dual = calculate_residuals(a_dual, k_nodes, params)
            J[:, j] = [res.der for res in R_dual]

        # Solve Newton step using least squares for robustness
        delta_a = np.linalg.lstsq(J, -R_val, rcond=None)[0]

        # Backtracking line search
        lambda_ = 1.0
        for _ in range(10): # At most 10 backtracking steps
            a_new = a + lambda_ * delta_a
            R_new = calculate_residuals(a_new, k_nodes, params)
            if np.linalg.norm(R_new, np.inf) < res_norm:
                a = a_new
                break
            lambda_ *= 0.5
        else: # If line search fails, take the small step and hope for the best
            a = a + lambda_ * delta_a

    # 5. Evaluate solution quality on a fine grid
    eval_grid = np.linspace(k_min, k_max, M)
    final_residuals = calculate_residuals(a, eval_grid, params)
    max_abs_residual = np.max(np.abs(final_residuals))

    return max_abs_residual

def solve():
    # Define the test cases from the problem statement.
    test_cases = [
        (0.33, 0.96, 0.08, 2.0, 1.0),   # Case A (baseline)
        (0.36, 0.99, 0.025, 2.0, 1.0),  # Case B (patient)
        (0.40, 0.95, 0.15, 3.0, 1.0),   # Case C (high depreciation and risk aversion)
        (0.33, 0.97, 0.08, 1.0, 1.0),   # Case D (log utility)
    ]

    results = []
    for case in test_cases:
        result = solve_for_case(case)
        results.append(result)

    # Final print statement in the exact required format.
    print(f"[{','.join(map(str, results))}]")

solve()

