# The complete and runnable Python 3 code goes here.
# Imports must adhere to the specified execution environment.

class Node:
    """Node definition for the multi-level singly linked list."""
    def __init__(self, val, next_node=None, child=None):
        self.val = val
        self.next = next_node
        self.child = child

    def __repr__(self):
        return f"Node({self.val})"

def _flatten_recursive(current):
    """
    Recursive helper that flattens the list starting at `current`
    and returns the tail of the flattened list.
    """
    if not current:
        return None

    tail = current
    while tail.next:
        tail = tail.next

    it = current
    while it:
        if it.child:
            original_next = it.next
            child_tail = _flatten_recursive(it.child)

            # Splice the flattened child list
            it.next = it.child
            it.child = None

            child_tail.next = original_next

            # Update the main tail if the new segment is at the end
            if original_next is None:
                tail = child_tail

            # Continue traversal from the end of the spliced segment
            it = child_tail
        else:
            it = it.next

    return tail

def flatten_recursive(head):
    """
    Main recursive function to flatten the list.
    It calls a helper that performs the actual an-place flattening.
    """
    if head:
        _flatten_recursive(head)
    return head

def flatten_iterative(head):
    """
    Iterative function to flatten the list in-place using a stack.
    """
    if not head:
        return None

    stack = []
    current = head

    while current:
        if current.child:
            # If there's a next node, save it to the stack to process later
            if current.next:
                stack.append(current.next)

            # Promote child to be the next node
            current.next = current.child
            current.child = None

        # If we reach the end of a chain and the stack has saved pointers
        if not current.next and stack:
            # Pop from stack to connect the chain
            current.next = stack.pop()

        # Move to the next node
        current = current.next

    return head

def build_structure(nodes_def):
    """
    Helper to build the list structure from a dictionary definition.
    `nodes_def` is a dict of val -> (next_val, child_val).
    """
    if not nodes_def:
        return None

    nodes = {val: Node(val) for val in nodes_def}
    for val, (next_val, child_val) in nodes_def.items():
        if next_val is not None:
            nodes[val].next = nodes[next_val]
        if child_val is not None:
            nodes[val].child = nodes[child_val]

    # Find head (node with no incoming edges, assumed to be the smallest value for simplicity)
    # This is a simplification; a more robust way would be to pass the head's value.
    in_degrees = {val: 0 for val in nodes}
    for _, (next_val, child_val) in nodes_def.items():
        if next_val is not None:
            in_degrees[next_val] += 1
        if child_val is not None:
            in_degrees[child_val] += 1

    head_val = min([val for val, deg in in_degrees.items() if deg == 0 and val in nodes_def])
    return nodes.get(head_val)


def build_from_case(case_def):
    """
    Builds the test case structure. 
    case_def is a tuple: (head_val, definition_dict).
    """
    if not case_def:
        return None
    head_val, nodes_def = case_def
    if not nodes_def:
        return None if head_val is None else Node(head_val)

    nodes = {val: Node(val) for val in nodes_def}
    for val, connections in nodes_def.items():
        next_val, child_val = connections
        if next_val in nodes:
            nodes[val].next = nodes[next_val]
        if child_val in nodes:
            nodes[val].child = nodes[child_val]
    return nodes.get(head_val)

def deepcopy_structure(head):
    """
    Performs a deep copy of the graph structure to ensure tests are independent.
    Uses a memoization dictionary to handle DAGs correctly.
    """
    if not head:
        return None

    memo = {}

    def copy_node(node):
        if not node:
            return None
        if id(node) in memo:
            return memo[id(node)]

        new_node = Node(node.val)
        memo[id(node)] = new_node

        new_node.next = copy_node(node.next)
        new_node.child = copy_node(node.child)

        return new_node

    return copy_node(head)

def get_list_values(head):
    """Converts a flattened linked list to a Python list of values."""
    values = []
    current = head
    while current:
        values.append(current.val)
        current = current.next
    return values

def solve():
    """
    Main function to define test cases, run flatteners, and verify results.
    """
    # Test cases defined as (head_value, {node_val: (next_val, child_val)})
    test_cases = [
        # Case 1: Empty structure
        (None, {}),
        # Case 2: Single node
        (1, {1: (None, None)}),
        # Case 3: General case
        (1, {
            1: (2, None), 2: (3, 7), 3: (4, 9), 4: (None, None),
            7: (8, None), 8: (None, 11), 11: (None, None),
            9: (10, None), 10: (None, None)
        }),
        # Case 4: Deep nesting
        (1, {
            1: (2, 4), 2: (3, None), 3: (None, None),
            4: (None, 5), 5: (None, 6), 6: (None, None)
        }),
        # Case 5: Multiple children
        (1, {
            1: (2, None), 2: (3, 6), 3: (4, None), 4: (5, 7), 5: (None, None),
            6: (None, None),
            7: (8, None), 8: (9, None), 9: (None, None)
        })
    ]

    results = []
    for case_def in test_cases:
        # Build two identical copies for recursive and iterative functions
        head1 = build_from_case(case_def)
        head2 = deepcopy_structure(head1)

        # Flatten using both methods
        result_head_rec = flatten_recursive(head1)
        result_head_iter = flatten_iterative(head2)

        # Convert to lists for comparison
        list_rec = get_list_values(result_head_rec)
        list_iter = get_list_values(result_head_iter)

        # Verify and store result
        if list_rec == list_iter:
            results.append(list_rec)
        else:
            results.append([-1])

    # Final print statement in the exact required format
    # The format is a string representation of a list of lists.
    # Ex: [[1, 2], [3, 4], [-1]]
    formatted_results = [f"[{','.join(map(str, res))}]" if res != [-1] else "[-1]" for res in results]
    print(f"[{','.join(formatted_results)}]")

solve()
