import sys

# Setting a higher recursion limit for potentially deep or large graphs,
# though not strictly necessary for the given test cases.
sys.setrecursionlimit(2000)

class Node:
    """Represents a node in a directed graph."""
    def __init__(self, label: int):
        self.label = label
        self.neighbors = []

    def __repr__(self) -> str:
        return f"Node({self.label})"

def build_graph(adj_map: dict[int, list[int]]) -> list[Node]:
    """Constructs a graph from an adjacency map."""
    all_labels = set(adj_map.keys())
    for neighbors in adj_map.values():
        for neighbor_label in neighbors:
            all_labels.add(neighbor_label)

    nodes = {label: Node(label) for label in sorted(list(all_labels))}

    for label, neighbor_labels in adj_map.items():
        node = nodes[label]
        node.neighbors = [nodes[neighbor_label] for neighbor_label in neighbor_labels]

    return list(nodes.values())

def copy_recursive(nodes: list[Node]) -> list[Node]:
    """Deep copies a graph using recursion (DFS)."""
    visited = {}  # Maps original node to its copy

    def _copy_recursive_helper(node: Node) -> Node:
        if node in visited:
            return visited[node]

        new_node = Node(node.label)
        visited[node] = new_node

        new_neighbors = []
        for neighbor in node.neighbors:
            new_neighbors.append(_copy_recursive_helper(neighbor))
        new_node.neighbors = new_neighbors

        return new_node

    # This loop handles disconnected components.
    for node in nodes:
        if node not in visited:
            _copy_recursive_helper(node)

    if not nodes:
        return []

    # Return copies corresponding to the input node list order.
    return [visited[node] for node in nodes]


def copy_iterative(nodes: list[Node]) -> list[Node]:
    """Deep copies a graph using an explicit stack (DFS)."""
    visited = {}  # Maps original node to its copy

    # This loop handles disconnected components.
    for node in nodes:
        if node not in visited:
            # Start a new traversal for a new component
            stack = [node]
            visited[node] = Node(node.label)

            while stack:
                current_original = stack.pop()
                current_copy = visited[current_original]

                for neighbor in current_original.neighbors:
                    if neighbor not in visited:
                        visited[neighbor] = Node(neighbor.label)
                        stack.append(neighbor)
                    current_copy.neighbors.append(visited[neighbor])

    if not nodes:
        return []

    return [visited[node] for node in nodes]


def are_structurally_equal(nodes1: list[Node], nodes2: list[Node]) -> bool:
    """Checks if two graphs are structurally equal."""
    if len(nodes1) != len(nodes2):
        return False

    map1 = {node.label: node for node in nodes1}
    map2 = {node.label: node for node in nodes2}

    if map1.keys() != map2.keys():
        return False

    for label in map1:
        node1 = map1[label]
        node2 = map2[label]

        neighbor_labels1 = sorted([n.label for n in node1.neighbors])
        neighbor_labels2 = sorted([n.label for n in node2.neighbors])

        if neighbor_labels1 != neighbor_labels2:
            return False

    return True

def are_identities_distinct(original_nodes: list[Node], copied_nodes: list[Node]) -> bool:
    """Checks if copied nodes are new objects in memory."""
    if not original_nodes and not copied_nodes:
        return True

    original_ids = {id(n) for n in original_nodes}
    copied_ids = {id(n) for n in copied_nodes}

    return original_ids.isdisjoint(copied_ids)

def solve():
    """Main function to run test cases and print results."""
    test_cases = [
        {},  # Case 1: Empty graph
        {1: [1]},  # Case 2: Single node with self-loop
        {1: [2], 2: [3], 3: [1]},  # Case 3: Single cyclic component
        {1: [2], 2: [3], 3: [1], 4: [5], 5: []},  # Case 4: Disconnected graph
    ]

    all_results = []

    for adj_map in test_cases:
        original_graph = build_graph(adj_map)

        rec_copy = copy_recursive(original_graph)
        iter_copy = copy_iterative(original_graph)

        b1 = are_structurally_equal(original_graph, rec_copy)
        b2 = are_structurally_equal(original_graph, iter_copy)
        b3 = are_identities_distinct(original_graph, rec_copy)
        b4 = are_identities_distinct(original_graph, iter_copy)
        b5 = are_structurally_equal(rec_copy, iter_copy)

        all_results.append([b1, b2, b3, b4, b5])

    # Format the output exactly as specified.
    print(f"[{', '.join(map(str, all_results))}]")

solve()
