# The complete and runnable Python 3 code goes here.
# Imports must adhere to the specified execution environment.
import numpy as np
from scipy.integrate import simpson, trapezoid

def solve():
    """
    Computes solvation free energy contributions for three test cases
    using numerical quadrature based on a staged alchemical protocol.
    """

    # Define the test cases from the problem statement.
    test_cases = [
        {
            "elec": {
                "lambdas": [0.0, 0.25, 0.5, 0.75, 1.0],
                "y_values": [0.0, 2.0, 4.0, 2.0, 0.0]
            },
            "vdw": {
                "lambdas": [0.0, 1.0/6.0, 2.0/6.0, 3.0/6.0, 4.0/6.0, 5.0/6.0, 1.0],
                "y_values": [0.0, 3.0, 5.0, 6.0, 5.0, 3.0, 0.0]
            }
        },
        {
            "elec": {
                "lambdas": [0.0, 0.5, 1.0],
                "y_values": [0.0, 0.0, 0.0]
            },
            "vdw": {
                "lambdas": [0.0, 0.1, 0.4, 1.0],
                "y_values": [0.0, 0.2, 0.8, 2.0]
            }
        },
        {
            "elec": {
                "lambdas": [0.0, 0.25, 0.5, 0.75, 1.0],
                "y_values": [0.0, 2.0, 1.0, 2.0, 0.0]
            },
            "vdw": {
                "lambdas": [0.0, 0.5, 0.9, 1.0],
                "y_values": [0.0, 1.0, 2.0, 2.0]
            }
        }
    ]

    def compute_integral(lambdas, y_values):
        """
        Calculates the integral based on the specified quadrature rules.

        - If the lambda grid is uniform and has an odd number of points, use Simpson's rule.
        - Otherwise, use the trapezoidal rule.
        """
        N = len(lambdas)

        # Check for uniform spacing with an absolute tolerance of 1e-12
        is_uniform = False
        if N > 1:
            diffs = np.diff(lambdas)
            if np.allclose(diffs, diffs[0], atol=1e-12, rtol=0):
                is_uniform = True

        # Apply the appropriate integration rule
        if is_uniform and N % 2 == 1:
            # Simpson's rule for uniform grid with odd number of points
            return simpson(y=y_values, x=lambdas)
        else:
            # Trapezoidal rule for non-uniform grid or even number of points
            return trapezoid(y=y_values, x=lambdas)

    all_results = []
    for case in test_cases:
        # Calculate electrostatic contribution
        df_elec = compute_integral(case["elec"]["lambdas"], case["elec"]["y_values"])

        # Calculate van der Waals contribution
        df_vdw = compute_integral(case["vdw"]["lambdas"], case["vdw"]["y_values"])

        # Calculate total free energy change
        df_total = df_elec + df_vdw

        # Append results in the specified order: total, elec, vdw
        all_results.extend([df_total, df_elec, df_vdw])

    # Format results to three decimal places and create the final output string
    formatted_results = [f"{res:.3f}" for res in all_results]

    # Final print statement in the exact required format.
    print(f"[{','.join(formatted_results)}]")

solve()
