import numpy as np

def solve():
    """
    Computes and compares numerical fluxes for the inviscid Burgers' equation.
    """

    # Test cases (u_L, u_R)
    test_cases = [
        (-1.0, 2.0),  # Case 1: Transonic rarefaction
        (-2.0, 0.5),  # Case 2: Transonic rarefaction
        (-0.5, 0.5),  # Case 3: Weak transonic rarefaction
        (0.0, 3.0),   # Case 4: Rarefaction touching sonic point (left)
        (-3.0, 0.0),  # Case 5: Rarefaction touching sonic point (right)
        (1.0, 3.0),   # Case 6: Non-transonic rarefaction
        (2.0, -1.0),  # Case 7: Shock
    ]

    results = []

    # Flux function for Burgers' equation
    def f(u):
        return 0.5 * u**2

    def compute_godunov_flux(u_L, u_R):
        """Computes the exact Godunov flux for Burgers' equation."""
        if u_L > u_R:  # Shock wave
            s = 0.5 * (u_L + u_R)
            if s >= 0.0:
                return f(u_L)
            else:
                return f(u_R)
        else:  # Rarefaction wave (or constant state)
            if u_L > 0.0:
                return f(u_L)
            elif u_R = 0.0:
                return f(u_R)
            else:  # u_L = 0  u_R (transonic rarefaction)
                return 0.0 # f(0.0)

    def compute_roe_flux(u_L, u_R):
        """Computes the Roe flux without entropy fix."""
        a_roe = 0.5 * (u_L + u_R)
        return 0.5 * (f(u_L) + f(u_R)) - 0.5 * abs(a_roe) * (u_R - u_L)

    def compute_roehh_flux(u_L, u_R):
        """Computes the Roe flux with the Harten-Hyman entropy fix."""
        a_roe = 0.5 * (u_L + u_R)
        diss_coeff = abs(a_roe)

        # Check for transonic rarefaction: u_L  0  u_R
        if u_L  0.0 and u_R > 0.0:
            lambda_L, lambda_R = u_L, u_R
            delta = 0.5 * (lambda_R - lambda_L)
            if abs(a_roe)  delta:
                diss_coeff = (a_roe**2 + delta**2) / (2.0 * delta)

        return 0.5 * (f(u_L) + f(u_R)) - 0.5 * diss_coeff * (u_R - u_L)

    def compute_hll_flux(u_L, u_R):
        """Computes the HLL flux with Einfeldt-type wave speeds."""
        lambda_L, lambda_R = u_L, u_R

        # Einfeldt wave speed estimates for scalar convex flux
        S_L = min(lambda_L, lambda_R)
        S_R = max(lambda_L, lambda_R)

        if S_L >= 0.0:
            return f(u_L)
        elif S_R = 0.0:
            return f(u_R)
        else: # S_L  0  S_R
            f_L, f_R = f(u_L), f(u_R)
            numerator = S_R * f_L - S_L * f_R + S_L * S_R * (u_R - u_L)
            denominator = S_R - S_L
            return numerator / denominator

    for u_L, u_R in test_cases:
        F_G = compute_godunov_flux(u_L, u_R)
        F_Roe = compute_roe_flux(u_L, u_R)
        F_Roe_HH = compute_roehh_flux(u_L, u_R)
        F_HLL = compute_hll_flux(u_L, u_R)

        e_roe = abs(F_Roe - F_G)
        e_hh = abs(F_Roe_HH - F_G)
        e_hll = abs(F_HLL - F_G)

        results.append([e_roe, e_hh, e_hll])

    # Format the final output string
    result_str = "[[" + "],[".join(",".join(map(str, triple)) for triple in results) + "]]"
    print(result_str)

solve()
