import numpy as np
from collections import defaultdict

def format_result(val):
    """Formats a number for the final output string."""
    if val == float('-inf'):
        return '-inf'
    return f"{val:.6f}"

def calculate_best_log_confidence(reads, k, alpha, s, t, L):
    """
    Computes the optimal log confidence for a gap fill.
    """
    # Step 1: Graph Construction
    kmer_counts = defaultdict(int)
    for read in reads:
        if len(read) >= k:
            for i in range(len(read) - k + 1):
                kmer = read[i:i+k]
                kmer_counts[kmer] += 1

    # Adjacency list representation: graph[u] = {v1: count, v2: count}
    graph = defaultdict(lambda: defaultdict(int))
    nodes = set()
    if k <= 1: # Nodes must have length k-1 >= 1
        return 0.0 if s == t else float('-inf')

    for kmer, count in kmer_counts.items():
        u, v = kmer[:-1], kmer[1:]
        graph[u][v] += count
        nodes.add(u)
        nodes.add(v)

    if not nodes:
        return 0.0 if s == t else float('-inf')

    node_list = sorted(list(nodes))
    node_to_idx = {node: i for i, node in enumerate(node_list)}
    num_nodes = len(node_list)

    # Step 2: Edge Weight Calculation
    weights = {}
    for u, neighbors in graph.items():
        if not neighbors:
            continue
        d_u = len(neighbors)
        sum_c_u = sum(neighbors.values())
        denominator = sum_c_u + alpha * d_u
        for v, c_uv in neighbors.items():
            numerator = c_uv + alpha
            prob = numerator / denominator
            weights[(u, v)] = np.log(prob)

    # Step 3: Dynamic Programming
    dp = np.full((L + 1, num_nodes), float('-inf'))

    if s in node_to_idx:
        s_idx = node_to_idx[s]
        dp[0][s_idx] = 0.0

    for l in range(1, L + 1):
        for u_str, u_neighbors in graph.items():
            u_idx = node_to_idx[u_str]
            if dp[l-1][u_idx] == float('-inf'):
                continue
            for v_str in u_neighbors:
                v_idx = node_to_idx[v_str]
                weight = weights[(u_str, v_str)]
                new_log_conf = dp[l-1][u_idx] + weight
                dp[l][v_idx] = max(dp[l][v_idx], new_log_conf)

    # Step 4: Final Answer Extraction
    if t not in node_to_idx:
        return 0.0 if s == t else float('-inf')

    t_idx = node_to_idx[t]

    # max log-confidence over paths of length 1 to L
    max_log_conf = np.max(dp[1:, t_idx]) if L > 0 else float('-inf')

    if s == t:
        # A path of length 0 (log-conf 0) is also an option.
        return max(0.0, max_log_conf)
    else:
        return max_log_conf

def solve():
    """
    Main function to run test cases and print results.
    """
    test_cases = [
        # Test case 1 (happy path, unique chain)
        {'reads': ['ATG', 'TGA', 'TGA', 'GAC'], 'k': 3, 'alpha': 0.5, 's': 'AT', 't': 'AC', 'L': 3},
        # Test case 2 (branching with unequal support)
        {'reads': ['ATG', 'ATG', 'ATA', 'TGA', 'TGC', 'GCA'], 'k': 3, 'alpha': 0.5, 's': 'AT', 't': 'GA', 'L': 2},
        # Test case 3 (no path exists)
        {'reads': ['AAA', 'AAT', 'ATC'], 'k': 3, 'alpha': 0.5, 's': 'GG', 't': 'TT', 'L': 5},
        # Test case 4 (boundary condition L=0 and s=t)
        {'reads': ['AAA', 'AAT', 'ATC'], 'k': 3, 'alpha': 0.5, 's': 'AT', 't': 'AT', 'L': 0},
    ]

    results = []
    for case in test_cases:
        result = calculate_best_log_confidence(
            case['reads'], case['k'], case['alpha'], case['s'], case['t'], case['L']
        )
        results.append(result)

    formatted_results = [format_result(r) for r in results]
    print(f"[{','.join(formatted_results)}]")

solve()
```
