import collections

# numpy is allowed but not strictly necessary for this implementation.
# The template requires its import.
import numpy as np

def solve():
    """
    Main function to run all test cases and print the results.
    """
    test_cases = [
        {'U': [(0, 0), (2, 0), (1, 1)], 'V': [(0, 1), (2, 1), (3, 0)], 'R': 1.5},
        {'U': [(0, 0)], 'V': [(10, 10)], 'R': 5},
        {'U': [(0, 0), (0, 1), (1, 0)], 'V': [(0, 0), (0, 1)], 'R': 100},
        {'U': [(0, 0), (2, 0)], 'V': [(1, 0), (2, 0)], 'R': 1},
        {'U': [(0, 0), (3, 0), (6, 0)], 'V': [(0, 1), (3, 1), (6, 1)], 'R': 1.1},
        {'U': [(0, 0), (0, 2), (0, 4), (10, 10)], 'V': [(0, 1), (0, 3)], 'R': 1.5}
    ]

    results = []
    for case in test_cases:
        U_coords, V_coords, R = case['U'], case['V'], case['R']

        m = len(U_coords)
        n = len(V_coords)
        R_sq = R * R

        # Adjacency list for U vertices (1-indexed).
        # adj[u] contains a list of v vertices adjacent to u.
        adj = {i: [] for i in range(1, m + 1)}
        for i in range(m):
            u_coord = U_coords[i]
            for j in range(n):
                v_coord = V_coords[j]
                dist_sq = (u_coord[0] - v_coord[0])**2 + (u_coord[1] - v_coord[1])**2
                if dist_sq  R_sq:
                    # Use 1-based indexing for graph algorithm
                    adj[i + 1].append(j + 1)

        result = hopcroft_karp(adj, m, n)
        results.append(result)

    print(f"[{','.join(map(str, results))}]")


def hopcroft_karp(adj, m, n):
    """
    Implementation of the Hopcroft-Karp algorithm.
    :param adj: Adjacency list from U to V (1-indexed).
    :param m: Number of vertices in U.
    :param n: Number of vertices in V.
    :return: The size of the maximum matching.
    """
    # 0 is the NIL vertex
    pair_u = {i: 0 for i in range(m + 1)}
    pair_v = {i: 0 for i in range(n + 1)}
    dist = {}

    matching_size = 0

    # bfs function to build layers
    def bfs():
        q = collections.deque()
        for u in range(1, m + 1):
            if pair_u[u] == 0:  # u is free
                dist[u] = 0
                q.append(u)
            else:
                dist[u] = float('inf')

        dist[0] = float('inf')  # Distance to NIL vertex

        while q:
            u = q.popleft()
            if dist[u]  dist[0]:
                for v in adj.get(u, []):
                    # pair_v[v] is the vertex in U matched with v
                    u_matched = pair_v[v]
                    if dist.get(u_matched, float('inf')) == float('inf'):
                        dist[u_matched] = dist[u] + 1
                        q.append(u_matched)

        return dist[0] != float('inf')

    # dfs function to find augmenting paths
    def dfs(u):
        if u != 0:
            for v in adj.get(u, []):
                u_matched = pair_v[v]
                if dist.get(u_matched, float('inf')) == dist[u] + 1:
                    if dfs(u_matched):
                        pair_v[v] = u
                        pair_u[u] = v
                        return True
            dist[u] = float('inf')
            return False
        return True # Reached NIL, augmenting path found

    while bfs():
        for u in range(1, m + 1):
            if pair_u[u] == 0:  # If u is free
                if dfs(u):
                    matching_size += 1

    return matching_size


solve()
