import copy
import sys

# Increase recursion limit for deep trees during validation.
sys.setrecursionlimit(2000)

class Node:
    """A node in the AVL tree."""
    def __init__(self, key):
        self.key = key
        self.left = None
        self.right = None
        self.height = 0

class AVLTree:
    """AVL tree implementation with methods for insertion, deletion, and validation."""

    def __init__(self):
        self.root = None

    def _height(self, node):
        return node.height if node else -1

    def _get_balance(self, node):
        if not node:
            return 0
        return self._height(node.left) - self._height(node.right)

    def _right_rotate(self, z):
        y = z.left
        T3 = y.right
        y.right = z
        z.left = T3
        z.height = 1 + max(self._height(z.left), self._height(z.right))
        y.height = 1 + max(self._height(y.left), self._height(y.right))
        return y

    def _left_rotate(self, y):
        z = y.right
        T2 = z.left
        z.left = y
        y.right = T2
        y.height = 1 + max(self._height(y.left), self._height(y.right))
        z.height = 1 + max(self._height(z.left), self._height(z.right))
        return z

    def insert(self, key):
        self.root = self._insert(self.root, key)

    def _insert(self, root, key):
        if not root:
            return Node(key)
        elif key < root.key:
            root.left = self._insert(root.left, key)
        else:
            root.right = self._insert(root.right, key)

        root.height = 1 + max(self._height(root.left), self._height(root.right))
        balance = self._get_balance(root)

        # Rebalance if necessary
        # Case 1: Left Left
        if balance > 1 and key < root.left.key:
            return self._right_rotate(root)
        # Case 2: Right Right
        if balance < -1 and key > root.right.key:
            return self._left_rotate(root)
        # Case 3: Left Right
        if balance > 1 and key > root.left.key:
            root.left = self._left_rotate(root.left)
            return self._right_rotate(root)
        # Case 4: Right Left
        if balance < -1 and key < root.right.key:
            root.right = self._right_rotate(root.right)
            return self._left_rotate(root)

        return root

    def _get_min_value_node(self, root):
        current = root
        while current.left is not None:
            current = current.left
        return current

    def bst_delete(self, key):
        """Performs BST deletion without rebalancing, only height updates."""
        self.root = self._bst_delete(self.root, key)

    def _bst_delete(self, root, key):
        if not root:
            return root

        if key < root.key:
            root.left = self._bst_delete(root.left, key)
        elif key > root.key:
            root.right = self._bst_delete(root.right, key)
        else: # Node to be deleted is found
            if root.left is None:
                return root.right
            elif root.right is None:
                return root.left

            # Node with two children
            temp = self._get_min_value_node(root.right)
            root.key = temp.key
            root.right = self._bst_delete(root.right, temp.key)

        if not root: # In case the tree becomes empty
            return root

        root.height = 1 + max(self._height(root.left), self._height(root.right))
        return root

    def is_balanced(self):
        """Checks if the entire tree satisfies the AVL property."""
        return self._is_balanced_recursive(self.root)

    def _is_balanced_recursive(self, root):
        if not root:
            return True

        balance = self._get_balance(root)
        if abs(balance) > 1:
            return False

        return self._is_balanced_recursive(root.left) and \
               self._is_balanced_recursive(root.right)

    def get_all_keys(self):
        """Returns a sorted list of all keys in the tree."""
        keys = []
        self._inorder_traversal(self.root, keys)
        return keys

    def _inorder_traversal(self, root, keys_list):
        if root:
            self._inorder_traversal(root.left, keys_list)
            keys_list.append(root.key)
            self._inorder_traversal(root.right, keys_list)

def solve():
    """Main function to solve the problem for all test cases."""
    test_cases = [
        [10, 5, 15, 3, 7, 13, 17],
        [8, 4, 12, 2, 6, 10, 14, 1, 3],
        [50, 25, 75, 10, 37, 62, 87, 5, 12, 31, 43, 57, 68, 81, 93],
        [30, 20, 40, 10, 25, 35, 50, 27, 26],
        [1, 2, 3, 4, 5, 6]
    ]

    final_results = []
    for insertion_sequence in test_cases:
        # 1. Build the initial AVL tree for the current case.
        avl_tree = AVLTree()
        for key in insertion_sequence:
            avl_tree.insert(key)

        # 2. Get a sorted list of all keys in the tree.
        all_keys = avl_tree.get_all_keys()

        # 3. Find the smallest key whose deletion doesn't require rotation.
        result_for_case = -1
        for key_to_delete in all_keys:
            # a. Create a deep copy to simulate deletion on.
            tree_copy = copy.deepcopy(avl_tree)

            # b. Perform standard BST deletion (without rebalancing).
            tree_copy.bst_delete(key_to_delete)

            # c. Check if the resulting tree is still balanced.
            if tree_copy.is_balanced():
                result_for_case = key_to_delete
                break  # Found the smallest key, move to the next test case.

        final_results.append(result_for_case)

    # Final print statement in the exact required format.
    print(f"[{','.join(map(str, final_results))}]")

solve()

