import numpy as np

def _calculate_similarity_matrix(adj, n):
    """Computes the Jaccard similarity matrix for node neighborhoods."""
    S = np.zeros((n, n), dtype=float)
    for i in range(n):
        S[i, i] = 1.0
        for j in range(i + 1, n):
            N_i = adj[i]
            N_j = adj[j]

            intersection_size = len(N_i.intersection(N_j))
            union_size = len(N_i.union(N_j))

            if union_size == 0:
                similarity = 0.0
            else:
                similarity = intersection_size / union_size

            S[i, j] = S[j, i] = similarity
    return S

def _get_similarity_clusters(S, n, tau):
    """Finds clusters as connected components of the thresholded similarity graph."""
    sim_adj = [set() for _ in range(n)]
    for i in range(n):
        for j in range(i + 1, n):
            if S[i, j] >= tau:
                sim_adj[i].add(j)
                sim_adj[j].add(i)

    visited = np.full(n, False, dtype=bool)
    clusters = np.full(n, -1, dtype=int)
    cluster_id = 0
    for i in range(n):
        if not visited[i]:
            queue = [i]
            visited[i] = True
            while queue:
                u = queue.pop(0)
                clusters[u] = cluster_id
                for v in sim_adj[u]:
                    if not visited[v]:
                        visited[v] = True
                        queue.append(v)
            cluster_id += 1
    return clusters

def _get_most_frequent_label(neighbor_labels):
    """Finds the most frequent label, with tie-breaking by smallest value."""
    if not neighbor_labels:
        return None

    counts = {}
    for label in neighbor_labels:
        counts[label] = counts.get(label, 0) + 1

    max_freq = 0
    # In Python 3.7+ dicts are insertion ordered. No assumptions made here.
    for count in counts.values():
        if count > max_freq:
            max_freq = count

    tied_labels = []
    for label, count in counts.items():
        if count == max_freq:
            tied_labels.append(label)

    return min(tied_labels)

def _run_lpa(adj, n):
    """Runs the deterministic Label Propagation Algorithm."""
    labels = np.arange(n)
    max_sweeps = 100

    for _ in range(max_sweeps):
        changed = False
        for i in range(n):
            neighbors = adj[i]
            if not neighbors:
                continue

            neighbor_labels = [labels[neighbor] for neighbor in neighbors]
            new_label = _get_most_frequent_label(neighbor_labels)

            if labels[i] != new_label:
                labels[i] = new_label
                changed = True

        if not changed:
            break

    return labels

def _calculate_nmi(partition_A, partition_B, n):
    """Computes the Normalized Mutual Information between two partitions."""
    unique_labels_A = np.unique(partition_A)
    unique_labels_B = np.unique(partition_B)
    num_clusters_A = len(unique_labels_A)
    num_clusters_B = len(unique_labels_B)

    map_A = {label: i for i, label in enumerate(unique_labels_A)}
    map_B = {label: i for i, label in enumerate(unique_labels_B)}

    contingency = np.zeros((num_clusters_A, num_clusters_B), dtype=float)
    for i in range(n):
        a_idx = map_A[partition_A[i]]
        b_idx = map_B[partition_B[i]]
        contingency[a_idx, b_idx] += 1

    p_ab = contingency / n
    p_a = np.sum(p_ab, axis=1)
    p_b = np.sum(p_ab, axis=0)

    # Calculate entropies
    h_a = -np.sum(p_a[p_a > 0] * np.log(p_a[p_a > 0]))
    h_b = -np.sum(p_b[p_b > 0] * np.log(p_b[p_b > 0]))

    # Handle zero entropy cases as per problem convention
    is_ha_zero = np.isclose(h_a, 0)
    is_hb_zero = np.isclose(h_b, 0)

    if is_ha_zero and is_hb_zero:
        return 1.0
    if is_ha_zero or is_hb_zero:
        return 0.0

    # Calculate mutual information
    i_ab = 0.0
    for r in range(num_clusters_A):
        for c in range(num_clusters_B):
            if p_ab[r, c] > 0:
                i_ab += p_ab[r, c] * np.log(p_ab[r, c] / (p_a[r] * p_b[c]))

    # Calculate NMI
    nmi = i_ab / np.sqrt(h_a * h_b)
    return nmi

def solve_one_case(n, edges, tau):
    """Processes a single test graph through all steps."""
    # Build adjacency list (list of sets for efficient operations)
    adj = [set() for _ in range(n)]
    for u, v in edges:
        adj[u].add(v)
        adj[v].add(u)

    # 1. Compute neighborhood-overlap similarity
    similarity_matrix = _calculate_similarity_matrix(adj, n)

    # 2. Get clusters from similarity graph
    partition_A = _get_similarity_clusters(similarity_matrix, n, tau)

    # 3. Get communities from LPA
    partition_B = _run_lpa(adj, n)

    # 4. Quantify consistency with NMI
    nmi = _calculate_nmi(partition_A, partition_B, n)

    return nmi

def solve():
    """Main function to run all test cases and print results."""
    E1 = {(0,1),(0,2),(0,3),(1,2),(1,3),(2,3),(4,5),(4,6),(4,7),(5,6),(5,7),(6,7)}
    E2 = E1.union({(3,4)})
    E3 = {(0,1),(1,2),(2,0)}

    test_cases = [
        (8, E1, 0.5),
        (8, E2, 0.5),
        (5, E3, 0.3)
    ]

    results = []
    for n, edges, tau in test_cases:
        result = solve_one_case(n, edges, tau)
        results.append(f"{result:.6f}")

    print(f"[{','.join(results)}]")

solve()
