import numpy as np
import math

def g_lambda(z: float, lam: float) - float:
    """Inverse Box-Cox transformation g_lambda(z)."""
    if abs(lam)  1e-12:
        return math.exp(z)
    base = lam * z + 1.0
    # Domain check is assumed valid at evaluation point by problem statement.
    return math.pow(base, 1.0 / lam)

def g1_lambda(z: float, lam: float) - float:
    """First derivative g'(z) for the inverse Box-Cox."""
    if abs(lam)  1e-12:
        return math.exp(z)
    base = lam * z + 1.0
    return math.pow(base, (1.0 / lam) - 1.0)

def g2_lambda(z: float, lam: float) - float:
    """Second derivative g''(z) for the inverse Box-Cox."""
    if abs(lam)  1e-12:
        return math.exp(z)
    base = lam * z + 1.0
    return ((1.0 / lam) - 1.0) * lam * math.pow(base, (1.0 / lam) - 2.0)

def g3_lambda(z: float, lam: float) - float:
    """Third derivative g'''(z) for the inverse Box-Cox."""
    if abs(lam)  1e-12:
        return math.exp(z)
    base = lam * z + 1.0
    return ((1.0 / lam) - 1.0) * ((1.0 / lam) - 2.0) * (lam ** 2) * math.pow(base, (1.0 / lam) - 3.0)

def solve():
    # Define the test cases from the problem statement.
    # Each tuple is (lambda, mu, sigma2, beta_j)
    test_cases = [
        (0.0, 0.0, 0.04, 0.2),    # Case 1
        (0.5, 1.5, 0.36, 1.0),    # Case 2
        (1.0, 3.0, 2.0, 2.0),     # Case 3
        (-0.5, 1.0, 0.25, 0.5),   # Case 4
    ]

    results = []
    for lam, mu, sigma2, beta_j in test_cases:
        g = g_lambda(mu, lam)
        g1 = g1_lambda(mu, lam)
        g2 = g2_lambda(mu, lam)
        g3 = g3_lambda(mu, lam)

        naive_pred = g
        bias_corr_pred = g + 0.5 * g2 * sigma2

        naive_effect = beta_j * g1
        bias_corr_effect = beta_j * (g1 + 0.5 * g3 * sigma2)

        results.append([naive_pred, bias_corr_pred, naive_effect, bias_corr_effect])

    # Final print statement in the exact required format.
    # Print a single line JSON-like list of lists with full-precision floats.
    def fmt(x):
        # Use repr-like formatting for better precision consistency
        return format(x, '.15g')
    line = "[" + ",".join("[" + ",".join(fmt(v) for v in row) + "]" for row in results) + "]"
    print(line)

if __name__ == "__main__":
    solve()
